@-moz-document domain("vk.com") {
#page_header .back{background:#4E729A!important;position:fixed!important;left:0;z-index:110}
#page_header .left{background:url(https://m.vk.com/images/vkn_logo.png) 16px 9px no-repeat!important;position:fixed!important;z-index:110}
#page_header .right{background:#4E729A!important;position:fixed!important}
#page_header .content{left:0;position:fixed!important}
#top_logo_down{background:url(https://m.vk.com/images/vkn_logo.png) 16px 9px no-repeat!important;margin-left:0!important}
.top_home_link{position:fixed!important;left:0}
#pad_wrap,#side_bar{position:fixed!important;margin-top:0!important}
#gp,#pad_arrow,#stl_side{display:none!important}
#pad_wrap{z-index:800!important;top:7px!important}
#pad_wrap[style*="top: 46px"]{right:6px!important;left:auto!important;top:46px!important}
#stl_text{margin-top:52px!important}
#pg_fixed{top:45px!important}
.fc_tab{background:rgba(222,229,235,.75)!important;border:2px solid #A5B5C7}
.fc_tab_title{color:#2B587A!important;text-shadow:0 1px 0 #A5B5C7!important}
.fc_tab_log,div.fc_tab_txt{box-shadow:0 0 3px #2B587A inset!important}
.fc_scrollbar_inner{background:#2B587A!important}
.fc_tab_close_wrap,.fc_tab_max_wrap,.fc_tab_min_wrap{background:#2B587A!important;box-shadow:0 0 2px #A5B5C7!important}
}
