css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')  

{

.controls_section li .followers:before 
{
position: absolute !important;
height: 30px !important;
line-height: 28px !important;
width: 107px !important;
font: inherit !important;
}
a.followers  div.hide_overflow
{
visibility: hidden !important;
}
.controls_section li .followers:before 
{
content: "Men of Letters" !important;
}

li.blog-sub-nav-item:nth-child(2) > a:nth-child(1) > div:nth-child(1):before
{
position: absolute !important;
height: 24px !important;
line-height: 28px !important;
width: 100px !important;
font: inherit !important;
background-color: #FFF !important;
}
li.blog-sub-nav-item:nth-child(2) > a:nth-child(1) > div:nth-child(1):hover:before
{
background-color: #E6EFF7 !important;
}
li.blog-sub-nav-item:nth-child(2) > a:nth-child(1) > div:nth-child(1):before
{
content: "Men of Letters" !important;
}

.controls_section li .posts:before
{
position: absolute !important;
height: 30px !important;
line-height: 28px !important;
width: 100px !important;
font: inherit !important;
}
a.posts  div.hide_overflow
{
visibility: hidden !important;
}
.controls_section li .posts:before
{
content: "Dad's Journal" !important;
}

li.blog-sub-nav-item:nth-child(1) > a:nth-child(1) > div:nth-child(1):before
{
position: absolute !important;
height: 24px !important;
line-height: 28px !important;
width: 100px !important;
font: inherit !important;
background-color: #FFF !important;
}
li.blog-sub-nav-item:nth-child(1) > a:nth-child(1) > div:nth-child(1):hover:before
{
background-color: #E6EFF7 !important;
}
.blog-sub-nav-details .blog-sub-nav-item.selected:hover
{
box-shadow: -4px 0px 0px #E6EFF7, 10px 0px 0px #E6EFF7 !important;
background: #E6EFF7 !important;
}
.blog-sub-nav-details .blog-sub-nav-item.selected
{
box-shadow: none !important;
background: rgba(0, 0, 0, 0) !important;
}
li.blog-sub-nav-item:nth-child(1) > a:nth-child(1) > div:nth-child(1):before
{
content: "Dad's Journal" !important;
}

.controls_section li .queue:before
{
position: absolute !important;
height: 30px !important;
line-height: 28px !important;
width: 100px !important;
font: inherit !important;
}
a.queue  div.hide_overflow
{
visibility: hidden !important;
}
.controls_section li .queue:before
{
content: "Research" !important;
}
  
li.blog-sub-nav-item:nth-child(5) > a:nth-child(1) > div:nth-child(1):before
{
position: absolute !important;
height: 24px !important;
line-height: 28px !important;
width: 110px !important;
font: inherit !important;
background-color: #FFF !important;
}
li.blog-sub-nav-item:nth-child(5) > a:nth-child(1) > div:nth-child(1):hover:before
{
background-color: #E6EFF7 !important;
}
li.blog-sub-nav-item:nth-child(5) > a:nth-child(1) > div:nth-child(1):active:before
{
background-color: #DCECF5 !important;
}
li.blog-sub-nav-item:nth-child(5) > a:nth-child(1) > div:nth-child(1):before
{
content: "Research" !important;
}

}
