css:
/* name: ultimate-guitar.com score page 2013/02 TEST */
@-moz-document url-prefix("http://tabs.ultimate-guitar.com/"){
/*= ultimate guitar.com tab score view =*/
/*- unwanted contents -*/
.ad_header_noad,
.ad_header_noad *
 {display:none !important;}

/*- page -*/
body {background: none !important; height: inherit !important;}
body {font-family: inherit !important; font-size: inherit !important;}
.page_handler {height: inherit !important; width: inherit !important;}
.page_handler>div {margin: 0 !important;}
body>center>div[style*="width:986px"] {width: 100% !important;} /* 2013/02/20 */


/*- header -*/
.header {border: none !important; font-size: inherit !important; margin: 0 !important;}
.h-menu {padding: 0 !important;}
.h-menu a {display: inline-block;}
.h-logo table, .h-logo tbody, .h-logo tr, .h-logo td,
.joinug, .joinug div { /* destroy table */
 display: inline-block !important;
 width: auto !important;
 min-width: 0px !important;
 max-width: none !important;
 height: auto !important;
 min-height: 0px !important;
 max-height: none !important;
 }
.logoplace {height: auto !important;}
.logolink {height: 48px !important; width: auto !important; background-position: 0 -8px;}
.login-col {background: none !important; border: none !important;}
.loginplace {height: auto !important; padding: 0 !important;}
.logintb td[style^="padding:0 5px"] {padding:0 !important;}
.joinug, .joinug div {margin: 0 !important; padding: 0 !important;}
/*- header > search box -*/
#advanced_search {margin: 0 !important;}
#advanced_search table,
#advanced_search tbody,
#advanced_search tr,
#advanced_search td { /* destroy table */
 display: inline-block !important;
 width: auto !important;
 min-width: 0px !important;
 max-width: none !important;
 height: auto !important;
 min-height: 0px !important;
 max-height: none !important;
 }
.t_header {width: 100% !important;}
.t_title, .t_t {height: auto !important;}
.t_title h1 {margin: 0 !important; padding: 0 !important;}
.t_versionspos {width: auto !important;} /* ver8 */
.t_title ins, .t_ts {
 height: auto !important; position: relative !important;
 right: inherit !important;
 top: inherit !important;
 width: auto !important;
 }
/*- song raw -*/
.page_border {
 border: none !important;
 width: 100% !important; height: inherit !important;
 margin: 0 !important;
 }
.page_bcg {
 width: 100% !important; height: inherit !important;
 }
.t_hs, /* horiz separator */
 .t_b, .ov_h {width: inherit !important; margin: 0 !important;}
.t_bl {width: 20% !important; min-width: 160px;} /* left col */
.t_br {width: 80% !important; min-width: 640px;} /* main col */

/*- commnet raw -*/
.cm_cnt {width: 100% !important; margin: 0 !important;}
/*- auto scroll controller 2013/02/20 -*/
.ascr {margin: 0 auto !important;}
.ascr>.autoscroll_table_handler {width: auto !important;}
.scrollhint, .scrollhint>div {width: auto !important;}
.autoscroll_controller_right, .autoscroll_controller_right {
 line-height: 1em !important;
 padding: 0 !important;
 text-align: center !important;
 }
/*- fixed panel 2013/02/20 -*/
#fixed_panel {left: auto !important; right: 0 !important; margin-left: auto !important;}
#fixed_panel.abs {right: 0 !important;}

}
