css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document url-prefix("http://tieba.baidu.com/p"),
url-prefix("http://tieba.baidu.com/f?ct="),
url-prefix("http://tieba.baidu.com/f?kz="),
url-prefix("http://tieba.baidu.com/f?z="),
url-prefix("http://c.tieba.baidu.com/p"){


/*==============
导航
===============*/
    #head a,#com_userbar a {
        color: #261CDC !important;
    }

    #head a.j_enter_ba {
        color: #fff !important;
    }

    #head a.j_search_post {
        color: #333 !important;
    }


    .nav_list a.nav_icon,.nav_wrap,.nav_list .space,.nav_list .focus,.nav_list li:hover,.nav_list li:hover .tbnav_tab_inner,.nav_list .focus .tbnav_tab_inner {
        background: none !important;
    }

    .nav_list,#star_class_nav {
        width: 97.6% !important;
        background: #00c !important;
    }

    .nav_wrap,.nav_list,.nav_list li,#star_class_nav,#star_class_nav li,.star_nav_wrap {
        height: 20px !important;
        line-height: 1.2 !important;
        font-size: 12px !important;
    }

    .nav_list li,#star_class_nav li {
        margin: 2px 20px 0 0 !important;
    }

    .nav_list,#star_class_nav {
        padding: 1px 0 0 18px !important;
    }

    .nav_list a,#star_class_nav a {
        color: #fff !important;
        padding: 0 !important;
        text-decoration: underline !important;
    }


/*==============
帖子内容
===============*/
.core_title_txt,.p_reply_first{
color: #261CDC !important;
font-weight:normal !important;
}
#j_core_title_go_top{
text-decoration:none !important;
}
body,.wrap1,.wrap2,.l_post {/* 屏蔽背景 */
background:none !important;
}
a#lzonly_cntn,a#quick_reply,p.j_lzl_p a,.repost_num a.shareCount,.file_item a.file_btn{
color:#333 !important;
text-decoration:none !important;
}
.core,.p_postlist,.thread_theme_4,.core_title_wrap,.star_collect_body,#tab_forumname,.v2_tab_tab_text,.pb_content{/* 屏蔽边框 */
border:none !important;
}
.core a,.j_lzl_c_b_a a,.l_thread_manage a,.sign_highlight,.p_author_name,.p_content a{
color: #261CDC !important;
text-decoration:underline !important;
}
.lzl_link_unfold,.p_tail_wap,.lzl_link_fold{
        color: #261CDC !important;
    }
.edit_font_normal{
color:#000 !important;
}
.p_content a:visited {
color: #800080 !important;
}
.louzhubiaoshi_wrap,.icon,.p_badge,#tofrs_down,.d_sign_split,.j_user_sign,.p_share_ding,.tieba_top10{
display:none !important;
}
.d_name:before{
content:"作者：";
margin:0 0 0 9px !important;
letter-spacing:0 !important;
}
.d_name{
display:inline;
}
.d_author{
margin:0 0 0 14px !important;
z-index:999 !important;
}
.d_author,.d_author_anonym{
text-align:left !important;
position:absolute !important;
bottom:-10px !important;
width:auto !important;

}
.p_content {
min-height: 0 !important;
margin:2px 0 -30px !important;
}
.d_post_content_main{
width:94%!important;
}

.share_thread{
margin:0 !important;
padding:0 !important;
}
p_author,.post_icon {
text-align: left !important;
}
.share_thread{
float:right !important;
}
#bdshare{
float:right !important;
}
.l_post {
border:none !important;
position:relative !important;
}
.core_reply_tail{
border-bottom: 1px solid #D9D9D9 !important;
}
.pager_theme_3 a, .pager_theme_3 span{
border:none !important;
margin-right:-2px !important;
}
.pager_theme_3 a:hover{
background:none !important;
}
.l_posts_num a{
margin-right:2px !important;
color: #261CDC !important;
text-decoration:underline !important;
}
#bdshare_tb{
float:right!important;
}
.j_lzl_container{
margin:0 0 33px !important;
}
    

#tb_rich_poster {
margin:20px 0 !important;
}
.edui-btn-toolbar {
margin:0 0 0 -13px !important;
}

    
/* 楼中楼 */
.core_reply_wrapper{
margin:0 0 45px 24px !important;
width:650px !important;
}

    
/* 屏蔽边框 */
#tb_nav,.pb_footer,#thread_theme_5,.thread_theme_7,.pager_theme_5 a, .pager_theme_5 span{
border:none !important;
}

/* 屏蔽背景 */
.nav_list a,.content,.pb_footer,.thread_theme_7,#thread_theme_5,.pb_content,.left_section,.tb_nav_sub_divide, .tab_picture_2, .tab_picture_1, .tab_picture, .tab_forumname, .tab_youliao, .v2_tab_tab, .v2_tab_tab_text,.l_post_bright,.left_section {
background:none !important;
}

/* 屏蔽垃圾 */
.l_post_bright[data-daid],.thread_recommend,.u_appcenterEntrance,.u_joinvip,.u_baiduPrivilege,.u_baiduPrivilege + .split,#contet_wrap li[data-daid],#forum_recommend,.firework_sender_wrap,.j_click_stats,.j_click_stats,.game_banner_common_wapper,.guessing_watermark,.u_tbmall,.u_tshow,.u_app + .split,.doupiao_offline,.save_face_bg,.banner_post,.tb_poster_placeholder,.dasense,#pb_adbanner,.card_top_wrap,.ui_bubble_right,.tbnav_tab_list,.p_mall_tail,.d_icons,.card_top,.d_nameplate,.share_btn_wrapper,.p_tail_font,.icon_meizhi,.tbnav_list,.tbui_fbar_square,.ui_triangle_inner,.ui_arrow_o_right,.tbui_fbar_props,.right_section,.tbui_follow_fixed,.edui-icon-medal,.banner_theme_2,.game_content,.j_surveillance,#search_baidu_promote,#j_navtab_game{
display:none !important;
}


}
