css:
@-moz-document url-prefix("http://up.subuya.com/") {
/* all */
body {font-family:'ヒラギノ角ゴ Pro W3','Hiragino Kaku Gothic Pro','メイリオ',Meiryo,'ＭＳ Ｐゴシック',sans-serif;max-width:940px;margin:0 auto;word-break:break-all;}

/* header */
p:nth-of-type(1) {font-size:10pt;text-align:center!important;}
p:nth-of-type(1):after {content:'鳩ろだ。';display:block;clear:both;font-size:40px;z-index:100;width:100%;float:left;margin:30px auto}
a[href="http://hatoya.tv/"], a[href="http://counter.fc2.com/"],#amzn_widget, div:nth-of-type(1), div:nth-of-type(2),body > hr:nth-of-type(2), body form hr:nth-last-of-type(1),
img[src*="fc2_adult.PNG"] {display:none!important}

/* thread */
form table {min-width:570px!important;max-width:980px!important;clear:left}
body > table {width:570px!important;margin:0 auto}
form table td[bgcolor="#F0E0D6"] {padding-right:-40px;border-radius:5px;padding-bottom:5px}
form table td[valign="top"] {display:none}
form table small, form > small {display:none}
center form table small {display:inline}

/* Ads */
nobr,form td iframe[src*="ads"], form td span, iframe[name="maad"],span[id*="ninja"],span[id*="yad"] {display:none!important}
}

@-moz-document url-prefix("http://up.subuya.com/siokara.php") {
form table:nth-of-type(6n+1) {display:none}
form table:nth-of-type(1) {width:570px!important;display:table}
}

@-moz-document url-prefix("http://up.subuya.com/cat.php") {
div:nth-of-type(1) {display:block!important}
}

@-moz-document url-prefix("http://up.subuya.com/view/") {
td{text-align:center}
img {max-width:90%}
}
