css:
@-moz-document domain("giantbomb.com") {
@font-face {
	font-family: Helvetica Neue;
	src: url('http://dl.dropbox.com/u/55335088/Websitez/HelveticaNeue.ttf');
}

@font-face {
	font-family: Myriad Pro;
	src: url('http://dl.dropbox.com/u/55335088/Websitez/MyriadPro-Regular.otf');
}

@font-face {
	font-family: Open Sans;
	src: url('http://dl.dropbox.com/u/55335088/Websitez/OpenSans-Regular.ttf');
}

.header-promo time {
  font-family: "Open Sans","Helvetica Neue","Arial",sans-serif;
 }

#nav-main > ul > li > a {
  font-size: 13px;
  font-family: "Myriad Pro", "Arial",sans-serif;
}
  
html[data-useragent*="Chrome"][data-platform="Win32"] #nav-main > ul > li > a {
font-weight: 700;
}
  .kubrick-info h2, .kubrick-info p {
      font-family: "Myriad Pro", "Arial",sans-serif;
    }

.message-content article p, .message-content article li, .message-content article div, .message-content .wysiwyg-body p, .message-content .wysiwyg-body li, .message-content .wysiwyg-body div {
  font-size: 12px;
  font-family: "Helvetica Neue", "Arial",sans-serif;
  }

h1 {
font-size: 32px;
line-height: 32px;
text-shadow: 1px 1px 2px #000;
}

h1, h2, h3, h4, h5, h6 {
margin: 0;
font-family: "Myriad Pro","Helvetica Neue","Arial",sans-serif;
font-weight: normal;
text-rendering: optimizeLegibility;
}

.gallery-box-pod dt a {
display: block;
font-family: "Helvetica Neue","Arial",sans-serif;
}

#forums .breadcrumbs {
font-family: "Open Sans","Helvetica Neue","Arial",sans-serif;
text-shadow: 1px 1px 2px #000;
}

html[data-useragent*="Chrome"][data-platform="Win32"] .sub-nav div > ul > li > a {
  font-weight:700;
}

.sub-nav div > ul > li > a {
    font-family: "Myriad Pro","Helvetica Neue","Arial",sans-serif;
  }

.message-title {
    font-family: "Helvetica Neue","Arial",sans-serif;
    font-weight: 400;
  	font-size: 12px;
  	line-height:10px;
  }


.kubrick-lead a:hover h2 {
color: #fff;
text-shadow: rgba(255,255,255,0.75) 0 0 5px !important;
}

.promo-strip a:hover h3 {
text-shadow: rgba(255,255,255,0.75) 0 0 5px,rgba(0,0,0,0.75) 0 1px 1px;
}
}
