css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("http://.*fimfiction.net/.*"){
span{
color:#ccc!important;
}

div.content div.content_box div.main div.dark_toolbar {
	background: rgb(22,22,22)!important;
	border-color:#161616!important;
}

div.private_messages div.light_block {
    background-color: rgb(24, 24, 24)!important;
}

#chapter_toolbar_container{
background:none!important;
}


div.comment div.data div.comment_information {
    text-shadow: 1px 1px rgb(0, 0, 0)!important;
}


div.light_toolbar {
    background: linear-gradient(to bottom, rgb(25, 25, 25) 0%, rgb(24, 24, 24) 50%, rgb(23, 23, 23) 51%, rgb(25, 25, 25) 100%) repeat scroll 0% 0% transparent!important;
    padding: 5px;
    font-size: 0.8em;
    border-bottom: 1px solid rgb(20, 20, 20)!important;
    font-family: Segoe UI,Arial,Sans-Serif;
}

#new_stories > h1,#latest_stories > h1{
color:#ccc!Important;
background:rgb(16,16,16)!important;
border-color:rgb(16,16,16)!important;
}
*{
    text-shadow: 1px 1px rgba(16, 16, 16, 0.8);
}

.story_image,div.right h1,#story_group_list > li > div,.story_list > li > div *,div.right .list_element_contents{
background:rgb(16,16,16)!important;
border-color:rgb(16,16,16)!important;
box-shadow:none!important;
border:none!important;
}

.chapters *{
color:#ccc!Important;
border:none!important;
}

.arrow{
display:none!important;
}
.message_no_image{
color:#ccc!Important;
background:rgb(16,16,16)!important;
border-color:rgb(16,16,16)!important;
}

div.inner_data,div.inner_data *{
color:#ccc!Important;
background:rgb(16,16,16)!important;
border-color:rgb(16,16,16)!important;
}

div.data *{
color:#ccc!Important;
background:rgb(25,25,25)!important;
border-color:rgb(25,25,25)!important;
}

div.data .comment_arrow{
display:none!important;
}

div.chapter_footer {
    color: rgb(250, 250, 250)!important;
    background-color: rgb(26, 26, 26)!important;
    border-top: 1px solid rgb(26, 26, 26)!important;
}

div.light_toolbar ul li:first-child a {
    background-color: rgb(26, 26, 26)!important;
    border-color: #000!important;
}

div.comment:nth-child(2n+1) {
    background-color: rgb(26, 26, 26)!important;
}

div.comment {
    background-color: rgb(36, 36, 36)!important;
    border-color:#000!important;
}

div.comment div.data {
    background-color: rgb(25, 25, 25)!important;
}

div.add_comment_toolbar {
    background-color: rgb(26, 26, 26)!important;
}



blockquote{
    box-shadow: 5px 5px 0px rgb(23, 23, 23)!important;
}


div.light_toolbar ul li:last-child a {
    background-color: rgb(26, 26, 26)!important;
    border-color: #000!important;
}


div.private_messages div.light_block div.box {
    background-color:#333!important;
    color: rgb(230, 230, 230)!important;
}

div.user_cp .message_table tr td {
    background-color:rgb(25, 25, 25)!important;
}


p{
color:#ddd!important;
}

div.content div.content_box {
    background-color: rgb(255, 255, 255)!important;
    border-radius: 2px!important;
}

div.content .pill_button:hover img {
    background-color: rgb(42, 42, 42)!important;
    border-color: rgb(40, 40, 40)!important;
}

div.content .pill_button img {
    background-color: rgb(42, 42, 42)!important;
    border-color: rgb(40, 40, 40)!important;
    
}

div.browse_page thead tr.story_item td {
    color:#ccc!important;
    background-color: rgb(22, 22, 22) !important;
    border-bottom: 1px solid rgb(0, 0, 0)!important;
}

div.browse_page tr.story_item td.author {
    border-right: 1px solid rgb(20, 20, 20)!important;
    background-color: rgb(24, 24, 24)!important;
    border-bottom: 1px solid rgb(0, 0, 0)!important;
}

div.browse_page tr.story_item td.story_data {
    background-color: rgb(38, 38, 38)!important;
    border-bottom: 1px solid rgb(0, 0, 0)!important;
}

div.front_page div.story_item {
    background-color: rgb(38, 38, 38)!important;
    border-bottom: 1px solid rgb(0, 0, 0)!important;
}

div.front_page div.featured_box div.right div.info {
    background-color: rgb(38, 38, 38)!important;
}

div.front_page ul.list li:nth-child(2n+1) {
    background-color: rgb(22, 22, 22)!important;
}

div.front_page ul.list li {
    background-color: rgb(22, 22, 22)!important;
}

div.content div.message {
    background-color: rgb(25, 25, 22)!important;
    border: 1px solid rgb(22, 21, 17)!important;
    color:#ccc!important;
    
    width:80%!important;
    margin:0px auto!important;
}

div.name{
    background-color: rgb(38, 38, 38)!important;
}

div.content .pill_button:hover{
    background-color: rgb(42, 42, 42)!important;
    border-color: rgb(40, 40, 40)!important;
}

div.content .pill_button{
    background-color: rgb(42, 42, 42)!important;
    border-color: rgb(40, 40, 40)!important;
    
}

div.content div.content_box div.main div.story div.left img.story_image {
    background-color: rgb(24, 24, 24)!important;
    border: 1px solid rgb(0, 0, 0)!important;
    box-shadow: 0px 0px 0px rgba(20, 20, 20, 0.75)!important;
}

div.content div.content_box div.main div.story div.categories {
    background-color: rgb(23, 23, 23)!important;
    border-bottom: 1px solid rgb(20, 20, 20)!important;
    border-left: 1px solid rgb(20, 20, 20)!important;
}


div.body_container {
    background-image: url('http://herpderp.com/QfjcWzy.png')!important;
    background: url('http://herpderp.com/QfjcWzy.png')!important;
}

div.content div.content_box div.main div.chapter_content div.textarea textarea chapter_content content_format_sans_serif content_format_wob content_format_big {
    width:80%!important;
}

div.nav_bar div.light div.drop_down_container div.container div.panel {
    background-color: rgb(22, 22, 22)!important;
    color: rgb(207, 152, 141)!important;
}

div.nav_bar div.light div.drop_down_container ul a:hover li {
    color: rgb(230, 230, 230)!important;
}

div.nav_bar div.light div.drop_down_container ul a li {
    color: rgb(200, 200, 200)!important;
}

div.content div.inner{
    max-width:85%!important;
}

div.content div.inner_margin  {
    width:80%!important; max-width:85%!important;
}

div.content div.content_box div.main div.chapter_content {
    max-width:100%!important;
}

div.content_box div.content_box_header {
    background-color: rgb(22, 22, 22)!important;
    color: rgb(212, 212, 212)!important;
    text-shadow: 1px 1px rgb(0, 0, 0)!important;
    box-shadow: 0px 1px rgb(22, 22, 22) inset!important;
    border-bottom: 1px solid rgb(22, 22, 22)!important;
}

div.content_box div.main{
    box-shadow: 1px 1px 1px 1px rgb(22, 22, 22) inset!important;
    background-color: rgb(22, 22, 22)!important;
}

div.content div.content_box div.main div.story div.right ul.chapters {
    border: 1px solid rgb(21, 21, 19)!important;
}

div.search_pane div.toggle_bar {
    margin-top:20px!important;
    width:80.25%!important;
    margin-left:9.85%!important;
}


h2 {
    background-color: rgb(22, 22, 22)!important;
    color: rgb(212, 212, 212)!important;
    text-shadow: 1px 1px rgb(0, 0, 0)!important;
}

div.content_box_header h2 a.report {
    background-color: rgb(45, 22, 22)!important;
    border: 1px solid rgb(45, 22, 22)!important;
}

div.rating_container a.like_button:hover, div.rating_container a.dislike_button:hover {
    background-color: rgb(22, 22, 22)!important;
}

div.rating_container a.like_button, div.rating_container a.dislike_button {
    background-color: rgb(22, 22, 22)!important;
}

div.rating_container div.bar_container {
    background-color: rgb(22, 22, 22)!important;
}



div.content_box_header div.right div.comment_container {
    text-shadow: 1px 1px rgb(0, 0, 0)!important;
}

div.track_container {
    background-color: rgb(100, 100, 100)!important;
    color: rgb(212, 212, 212)!important;
    text-shadow: 1px 1px rgba(0, 0, 0, 0.75)!important;
}


a{
    color: rgb(252, 252, 252)!important;
    text-shadow: 1px 1px rgba(0, 0, 0, 0.75)!important;
}

a:hover{
    color: rgb(252, 252, 252)!important;
    text-shadow: 1px 1px rgba(0, 0, 0, 0.75)!important;
}

div.page_list li a {
    border-right: 1px solid rgb(22, 22, 22)!important;
    color: #ccc!important;
    background-color: rgb(22, 22, 22)!important;
}


div.content div.page_list ul li:first-child a {
    border-left: 1px solid rgb(22, 22, 22)!important;
}

div.content div.content_box div.main div.story div.right ul.chapters div.chapter_container li {
    background-color: rgb(28, 28, 28)!important;
    border-top:1px solid rgb(50, 50, 50)!important;
}

div.chapter_container.chapter_expander{
    background:rgb(100,100,100)!important;
    background-color: rgb(34, 34, 34)!important;
}

div.content div.content_box div.main div.story div.right ul.chapters li {
    background-color: rgb(28, 28, 28)!important;
}

div.content div.content_box {
    position: relative;
    margin-bottom: 16px;
    margin-top: 16px;
    box-shadow: 5px 5px 20px rgba(0, 0, 0, 0.1);
    border: 1px solid rgba(0, 0, 0, 0.3);
    background-color: rgb(28, 28, 28)!important;
}


div.rating_container {
    color: rgb(212, 212, 212)!important;
    text-shadow: 1px 1px rgb(0, 0, 0)!important;
}


div.page_list {
    background-color: rgb(22, 22, 22)!important;
    border: 1px solid rgb(22, 17, 17)!important;
}

div.right,div.rating_container > *,div.rating_container,div.comment_container > *{
    background:none!important;
    border:none!important;
    border-color:#000!important;
    color:#ccc!important;
}

a.expand_right{
    background:none!important;
}

div.left *{
    color:#ccc!important;
    background-color: rgb(22, 22, 22)!important;
}

#chapter_container *{
    color:#ccc!important;
}
}
