css:
@-moz-document url-prefix("https://twitter.com") {
*,input,textarea {font-family:Tahoma !important; font-size: 11px; }
.global-nav-inner {background: #222;}
.nav>li {color: #eee;}
.wtf-module.has-content,
.module.trends,
.Footer.module {display: none;}
.home-tweet-box, .rosetta .dm-tweetbox, .rosetta .WebNotification-buffer--altColor, .content-main .conversations-enabled .expansion-container .inline-reply-tweetbox,#global-tweet-dialog .modal-footer {background: #eee;}
.tweet-box[contenteditable="true"]{border-color: #aaa;}
.with-media-forward .cards-base .media-forward {max-height: 0;}
.stats a strong {font-size: 14px;text-align:center;}
div.dashboard.dashboard-right {display: none;}
body.three-col .wrapper {width: 900px;}
strong.fullname.js-action-profile-name.show-popup-with-id {font-size: 12px;}
}
