css:
@-moz-document domain("userstyles.org") {
body{background-color:black !important; color:white !important;}
header{background:black !important; color:lime !important; border:2px solid lime;}
header a,header a:visited,header a:active{color:lime !important;}
.lb-outerContainer {background-color:rgb(50,50,50) !important;}
header a:hover{color:orange !important;}
#left-sidebar nav{color:orange !important; background:transparent !important; border:2px solid orange; font-weight:bold !important;}
nav a,nav a:visited, nav a:active{font-weight:normal !important; color:white !important;}
nav a:hover{color:red !important;}
#search{color:magenta !important; border:2px solid magenta; background:black !important;}
#search-terms,#search-submit{border-color:magenta !important; background:black !important; color:magenta !important; border-radius:3px !important;}
#stylish-code{color:black !important;}
a{color:orange !important;}
a:visited{color:orangered !important;}
a:hover{color:red !important;}
a:active{color:lime !important;}
table.author-styles, table.author-styles th,table.author-styles td{border:1px solid grey !important; border-radius:3px !important;}
#license{border:2px solid orange !important; border-radius:3px !important; padding:3px !important; margin-top:5px !important;}
#license p{margin:0px !important;}
.license-title{color:lime !important; font-weight:bold !important;}
#below-install-ad,#post-install-ad,.ad,#related{display:none !important;}
.style-brief-text header{border:0px !important; color:lime !important; font-weight:bold !important; text-decoration:underline !important;}
article.bad-rating{border:1px solid red !important; border-radius:3px !important;}
article.good-rating{border:1px solid lime !important; border-radius:3px !important;}
article.no-rating{border:1px solid grey !important; border-radius:3px !important;}
article.ok-rating{border:1px solid green !important; border-radius:3px !important;}
#widget-code{color:black !important;}
#obsoletion-message{background:transparent !important; border:2px solid red !important;}
#obsoletion-message h2{color:red !important;}
section, article > .in-section-ad{border:2px solid white !important; border-radius:5px; padding:15px 5px 10px 30px !important; margin-bottom:5px !important;}
#left-info{margin-top:10px;margin-left:10px;}
u{color:rgb(200,200,200) !important;}
h2{font-weight:bold !important;font-style:normal !important; color:lime !important; font-size:22px !important;}
.style-brief-stats > span{color:rgb(200,200,250) !important;}
.style-brief{padding:5px !important;}
#Form_Discussion{background:#111 !important; border:2px solid cyan !important; border-radius:4px !important;}
.InputBox, .BigInput {color:orange !important; background:#222 !important;}
.TextBox,.textarea{color:white !important; background:#222 !important;}
.Button{color:black !important; background:cyan !important; transition:box-shadow 0.3s ease !important;}
.Button:hover{box-shadow:0px 0px 6px 3px cyan;}
div.Search input[type='text']{border:1px solid lime !important; box-shadow:0px 0px 2px 2px lime !important; color:white !important; transition:all 0.5s ease !important;}
div.Search input[type='text']:focus{border:1px solid magenta !important; box-shadow:0px 0px 2px 2px magenta !important; color:white !important;}
}

@-moz-document domain("forum.userstyles.org") {
#Head{background:black !important; border-bottom:2px solid lime !important;}
.DiscussionsTabs, .HeadingTabs, .DiscussionTabs, .FirstPage,.Box,.PanelInfo, .PanelCategories li,.ProfileTabs,.PanelInfo li,.ActivityTabs,.ConversationsTabs{background:black !important;}
a.TabLink, a.TabLink:visited,a.BigButton,a.BigButton:visited{background:rgb(61, 59, 59) !important; border:1px solid rgb(89, 85, 85) !important; color:lime !important; text-shadow:none !important;}
a.TabLink:hover,a.BigButton:hover{color:cyan !important;}
a.TabLink:active,a.BigButton:active{color:red !important;}
.MessageForm,.CommentForm{background:rgb(48, 31, 31) !important; border-radius:3px !important;}
.SubTab{font-size:18px !important;}
span.Title{color:grey !important;}
#Popup{color:black !important;}
*{color:white !important;}
.Mine{background:#222 !important;}
#MessageForm,#Form_To{background:#333 !important;}
#Form_Conversation{background:#111 !important;}
#Form_StartConversation,.CommentButton{background:cyan !important; color:black !important;}
.UserQuote{background:#222 !important;}
div.Message pre{background:limegreen !important; color:black !important;}
div.Message pre a {color:blue !important;}
code {background:chocolate !important; color: white !important;}
#Menu a span {background:#222 !important; color:lime !important; font-weight:bold !important;}
.DataList .Meta strong{border:1px solid cyan !important; background:#222 !important; color:white !important;}
#Content .SplashInfo{background:#222 !important;}
}
