@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("rutracker.org") {

/* HEADER TOP FIX */
  HTML {overflow-x:auto !important; padding-top:50px !important;}
  .topmenu {border-bottom:1px solid #CFD4D8 !important; height:27px !important; overflow:hidden !important;}
  #page_header {background:#FFFFFF !important; padding-bottom:3px !important; position:fixed !important; top:0 !important; z-index:999 !important;}
  #main-navy {border-width:1px 0px 0px 0px !important;}

/* AD & SITE CLEANER */
  DIV.post_wrap {max-width:95% !important; overflow:auto !important;}

  IMG.smile,
  IFRAME,
  FORM[action="thx.php"],
  FORM[action="http://www.jobsmarket.ru"],
  [src*="static.rutracker.org/avatars/"],
  DIV[style="padding: 8px 0 0 0;"],
  DIV[style="margin: 4px auto 6px; text-align: center;"],
  DIV[style="padding: 8px 0 0;"][class="tCenter"],

  .signature,
  .sig-sep,
  .posts,
  .joined,
  .from,
  .flag,
  .thx-container,
  
  #page_footer,
  #logo,
  #latest_news,
  #board_stats,
  #ddest-bn-1,
  #glad-1,#glad-idx,
  #idx-3,
  #bn-9d-1,#bn-tio-1,
  #begun-t-user,#begun-tr,

  #adriver-468x60,#adriver-240x120,#rle-468x60,#qle-468x60,#looky-468x60,
  #bn-nikita-1,#bn-nikita-2,#bn-nikita-3,#bn-nikita-4,#bn-nikita-5,#bn-nikita-6,
  #ad-all_bottom,#ad-index_left_1,#ad-index_left_2,#ad-index_left_3,#ad-index_left_4,
  #owo-idx-1,#owo-idx-2,#owo-idx-3,#owo-idx-4,#owo-idx-5,#owo-idx-6,
  #mg-informers,#fp-banner,
  #copyright,.copyright
  {
  display: none !important;}

  tr[id]:HOVER {background-color: #cccccc !important;}
 
}
