css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("talk.lg.ua") {

div.desc > span.forumdesc, span.postdetails br, a[href*="liveinternet"], .copyright, iframe, embed, table[style="background: #536275;"], .lastaction br, body > table, div[style*="width:468px;"] {
    display: none !important;
}

.lastaction a {
    padding-left: 5px !important;
}

a[href*="&view=getnewpost"] > img, [id*="tid-span-"], .mi-nipagelink, a[hr-ef*="multi_page_jump"], span[style*="color:red"], font[color="magenta"] {
    float: left !important;
    margin-right: 5px !important;
}

div.desc {
    float: right !important;
}

div, p, span.minipagelink, #ipbwrapper, td, #navstrip {
    margin-top: 0 !important;
    margin-bottom: 0 !important;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
}

table, td {
    border-spacing: 1px !important;
    border: 0px !important;
}

div.postc1, div.postc2, div.postc3, div.postc4, div.postc5, div.postc6, div.postc7 {
    background: none !important;
}

div.info {
    line-height: 10px !important;
    font-size: 10px !important;
}


}
