@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {

/* --------------- base --------------- */
* { font-family : Verdana, arial, san-serif !important; }
button { font-size: 12px !important; }

/* --------------- Header Bar --------------- */

/* Header Bar */
.fgrX7c{
background-color : #deebff !important;
height : 24px !important;
}

/* Header Bar Links */
#gbar a, #gbar a:active, #gbar a:visited, .l73JSe{
font-size : 12px !important;
color : #515459 !important;
text-decoration : none !important;
}

/* Sign Out */
.OZly4d,[id="1fbt"]{
color:#cc0000 !important;
font-weight:bold !important;
}

/* --------------- GMail Bar --------------- */

/* Search Mail */
.oF1kyb input {
margin-right : 2px !important;
width : 320px !important;
border: 1px solid #3d3a66 !important;
background-color: #ffffff !important;
font-size : 15px !important;
-moz-appearance: none !important;
}

/* Remove the button to "Search the Web" */
div[class="tYBwhe"] button:last-child { display: none !important; }

/* Filter & Options Links */
.SUicbd{
font-size : 9px !important;
color : #515459 !important;
padding-top:3px;
}

/* create a filter & search options */
.gBJOMe, .gBJOMe{
background-color: #ffffff !important;
font-size : 15px !important;
color : #515459 !important;
}

/* --------------- left menu --------------- */

/* remove menu icons */
.aBJ8he,.gUlKtd { display: none !important;}

/* Menu Positioning */
.NIPhib .zD5BAe{
text-decoration : none !important;
background-color : transparent !important;
padding : 2px 0 2px 15px !important;
font-family : Verdana, arial, san-serif !important;
font-size : 14px !important;
color : #515459 !important;
display: block !important;
}

/* Item */
.XoqCub .NIPhib{
margin-left : -4px !important;
width : 138px !important;
background : #ffffff !important;
background-image : url('data:image/gif;base64,R0lGODlhCQAPAIABAMHP4////yH5BAEAAAEALAAAAAAJAA8AAAIXhINpl7jQ3IrhURtxm5tL9kGKV5HaRxUAOw')!important;
background-repeat: no-repeat !important;
background-position: 3px 65% !important;
}

/* Hovered */
.XoqCub .NIPhib:hover{
margin-left : -4px !important;
width : 138px !important;
background : transparent !important;
background-image : url('data:image/gif;base64,R0lGODlhCQAPAIABALRDZ////yH5BAEAAAEALAAAAAAJAA8AAAIXhINpl7jQ3IrhURtxm5tL9kGKV5HaRxUAOw')!important;
background-repeat: no-repeat !important;
background-position: 3px 45% !important;
font-weight: bold !important;
}

/* Selected */
.cBhtOe .NIPhib {
background-color : transparent !important;
background-image : url('data:image/gif;base64,R0lGODlhCQAPAIABALRDZ////yH5BAEAAAEALAAAAAAJAA8AAAIXhINpl7jQ3IrhURtxm5tL9kGKV5HaRxUAOw')!important;
background-repeat: no-repeat !important;
background-position: 3px 45% !important;
z-index : 100 !important;
}

/* Compose Mail & Contacts */
div[class="XoqCub cOSVMd ACpQre"] div.NIPhib .zD5BAe {
background: #ffffff !important;
margin: 0px !important;
padding: 0px !important;
color: #0069bb !important;
font-weight: bold !important;
}

/* Remove "Chats" Menu item */
.ybaOyc > div > div:first-child + div +div { display: none !important }

/* Remove Chat Box */
div[class='XoqCub a3hTGd'] { display: none !important; }

/* Hide Labels Box */
div[class="XoqCub Tzvkxd"], .XPj4ef  { display: none !important; }

/* Remove Invite a Friend */
.I94Sdc { display:none !important;}

/* --------------- inbox --------------- */

/* Neaten the Main Frame */
table[class*="PwUwPb XoqCub MMcQxe"],
table[class="NQNkZd"],
table[class="N2mZ7b"] {	width: 99% !important; }
div[class="Ch5Hj"] { margin-right:1% !important; }

/* more action & select */
select, .QOD9Ec {
margin-top: 3px;
background-color : #ffffff !important;
border : 1px solid #000 !important;
font-size : 11px !important;}

/* unread mail */
.QhHSYc{
background-color : #deebff !important;
font-size : 1.2em !important;
color : #515459 !important;
}

/* read mail */
.AnqB9d{
background : #ffffff !important;
font-size : 1.2em !important;
color : #515459 !important;
}

/* selected mail*/
.rfza3e {
background-color : #ffffcc !important;
color : #515459 !important;
}

/* hover item */
tr[class^="xweT7d AnqB9d"]:hover,
tr[class^="xweT7d QhHSYc"]:hover {
background-color : #dddddd !important;
}

/* Date/Time Thinner */
.bLKGFb{ height:28px !important; width: 52px !important; }

/* Hide Labels */
.MgLW8b,.Bdmgc,.Ir5Jyf,.C5uTre {display:none !important; }

/* Remove Quick Contacts Infocard */
.FVNmUc { display: none !important }

/* Widen & Align Inbox Items */
.AnqB9d td {padding-top:4px !important;  padding-bottom:4px !important; vertical-align:middle !important }

/* --------------- read mail --------------- */

/* Remove Ads */
td[class="eWTfhb"]>div[class="XoqCub"]>div[class="XoqCub"]:last-child { display: none !important; }

/* Remove Quick Links */
td.eWTfhb>div[class="XoqCub"]>div.XoqCub>div.yMuNaf {	display: none !important; }

/* Remove Bottom TextArea */
textarea.gV2Gwc { display:none !important; }

/* Widen Mail Window */
td.eWTfhb > .XoqCub{
width : 99% !important;
overflow : visible !important;}

/* Remove Mail Title */
h1.YfMhcb>span { display: none !important; }

/* Style "Images are not display" Bar */
.XwckWe{
margin-top: 10px !important;
padding: 6px !important;
background-color: #ffffcc !important;
border-bottom: 1px solid #ccc !important;
border-top: 1px solid #ccc !important;
}

/* Mail Font Size */
.VrHWId, .EP8xU, .ckChnd, .A5liqb, .X5Xvu, .D05ws{
font-size : 15px !important;
}

/* text links */
.YrSjGe a{color : #0069bb !important;}
.YrSjGe a:hover, .YrSjGe a:visited{
color : #cc0000 !important;}

/* --------------- footer --------------- */

/* Remove Footer Ads */
.s7hnoe,.Ax9Ooe,.tBLpAf { display: none !important; }

/* Quota Color */  
.LaiJff { color: #0069bb !important; }

/* --------------- Updates v1.1 --------------- */

/* Remove Invite on Sent Mail */
.YCDlS { display: none !important; }

/* White Background for Reply Editor */
.iE5Yyc { background: #ffffff !important; }

/* other input, textarea also white */
.mMl8gd, .rSfjbb, .mMl8gd, .MtafId, .ReKnmb input, .Z5xHXe input,
.kN8OP input, .tAYBjb textarea, .yolZre input, .pjRZl,
.GorKne select, .manager-page select, .qIKyDc,
.GPKGxc input, .GPKGxc textarea, .WwReqb input, .ccqOyd textarea,
.zrlMEb input{
background-color: #ffffff !important;
-moz-appearance: none !important;
}

/* Notices Highlight */
.m14Grb { background: #ffffff !important; }

/* --------------- Updates v1.2 --------------- */

/* Deleted Messages Background */
.dyGHQb { background: #ffffff !important; }

}
