css:
@-moz-document url("http://www.baidu.com/"), url-prefix("http://www.baidu.com/index.php") {
* {
	color: white !important;
	text-decoration: none !important;
}
body {
	background: url(/*[[pgbg]]*/);
  background-size: 100%;
  background-color:/*[[dfcl]]*/ !important;
}
#s_wrap,#u,#lg,a[href*="more"] {
	display: none;
}
#m,div>table,div[id*='sd_'] {
	background: /*[[bg]]*/ !important;
}
/* 搜索建议框优化*/
	div[id*='sd_'] {
	position: fixed !important;
	left: 0px !important;
	top: 0px !important;
	width: 150px !important;
	border: none !important;
	/*display: block !important;*/
	background-image: url(data:image/png;base64,) !important;
	background-repeat: no-repeat !important;
	border-right: 1px solid white !important;
	border-bottom: 1px solid white !important;
}
#st {
	width: 150px !important;
	table-layout: fixed;
	margin-top: 40px;
}
td {
	overflow: hidden;
	text-overflow: ellipsis;
	word-break: keep-all;
	white-space: nowrap;
}
.ml,.mo {
  background:none !important;
	color: white !important;
}
.ml:hover,.mo:hover{
	background: orange !important;
}
.ml:active,.mo:active {
	background: rgb(112,202,255) !important;
}
b {
	font-weight: normal;
}
/* 搜索区域*/
	#nv {
	text-indent: 115px;
}
#su,#kw {
	background: none;
	border: none;
}
#kw {
	border-bottom: 1px solid /*[[ftcl]]*/;
color:/*[[ftcl]]*/ !important;
}
#su {width: 100%;
	height: 100%;
}
/* bug*/
#s_menu {
	display: none;
} /*[[ftcl2]]*/
}
