css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(webplanet.ru) {
/* faxenoff */
table td.menu, .gray_menu, h4 img, .more-link, a.read_more, a.print, .node h2.title img,
#block-archive-0, #block-search-0, #block-webplanet-4, #block-webplanet-1, #block-webplanet-2,
#block-block-2, div.old_news, #block-block-1, #block-block-5, h1.title img, div.podval, a.rek,
div.copy, #counters, #top-banner{display: none !important;}

#top_navigation{background: #eee !important; background-image: none; position: absolute; right: 0; top: 0;}
td{vertical-align: top; width: 0px;}
#logo{margin-left: 40% !important;}
.old_news{ background-color: red !important;}

h1.term, h3.term, h2.title{background-color: #fff !important; background-image: none !important; border-bottom: 1px solid #666;
color: #aaa !important; font-size: 22pt !important; font-weight: normal !important; letter-spacing: -2px;
margin-bottom: 10px !important; line-height: 24pt !important;}

.parts, .parts2, .news{padding-left: 10px;padding-right: 10px;}

a.xml-icon{float: right; padding-top: 18px !important;}

.column_item, .detail_item, .news_item, div.service_info{background-color: #fff !important; border: 0 !important;}
.news_item, .column_item, .detail_item{padding-bottom: 16px !important;}
.service_info{margin-top: 4px !important;}

div.content a img{border: 1px solid #666 !important;}
.service_info a{color: #333 !important;} .service_info a:hover{color: #000 !important; background-color: #fff !important;}
.comment{border: 0 !important;} .comment .service_info{padding-bottom: 3px !important;}

.article_body, .article_teaser, .article_list{border: 0 !important;}
td.main div.comment{position: relative;background-color: #fff !important; border-top: 1px solid #ddd !important;}

div.comment .service_info{position: absolute !important; bottom: 0; padding-left: 5px !important;}
div.comment .content{padding-bottom: 8px !important;}

.more a, .submitted a, div.comment .service_info a{color: #000 !important; font-weight: bold !important;}
.more a:hover, .submitted a:hover, div.comment .service_info a:hover{color: #800 !important; text-decoration: underline !important;}

td.main{border-left: 40px solid #fff !important;}

.node h2.title a, .column_item h4 a, .detail_item h4 a, #main-page h4 a{text-decoration: none !important; color: #000 !important;} #main-page h4 a:hover, #main-page .node h2.title a:hover{color: #800 !important;}
.node h2.title a, .node h2.title{letter-spacing: 0px !important; font-size: 12pt !important;}

div.links{ border-left: 1px solid #eee; float: right; padding: 3px 20px; margin-top: -20px; position: relative;}
div.links a{color: #ddd !important; font-size: 8pt !important;} div.links a:hover{color: #000 !important;}

.comment_form{border: 1px solid #ddd !important;}
}
