@-moz-document domain("powned.tv") {

	#leaderboard 
	{
		display: none!important;
	}

	body 
	{
		font:11px "Trebuchet MS","Lucida Sans unicode",Arial,Helvetica,sans-serif,courier!important;
	}
        #uitgelicht {
                padding: 3px 0!important;
        }

	#uitgelicht li 
	{
		height:165px!important;
	}

	.articlelist li, .uitzendinglist li 
	{
		line-height:1.3!important;
	}

	#maincol-med 
	{
		background-color:#FFF !important;
		color:#000000 !important;

	}

	#maincol-med .artikel-intro, #maincol-med .artikel-main 
	{
		font-size: 13px!important;
