css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://destiny.bungie.org/forum/index.php?mode=thread&id=") {

/************************
 ** header of the post **
 ************************/
.thread-posting .header {
    text-align: right !important; /* put author info on right (float doesn't work) */
    padding: 2px 10px 2px 10px !important; /* reduce header area */
}

.thread-posting .header h1, h2 {
    font-size: 0.8em !important; /* smaller font */
    display: inline !important; /* to get author info on same line */
    float: left !important; /* keep title on the left */
    text-align: left !important; /* for really narrow window */
}

.thread-posting .header .author {
    font-size: 0.7em !important; /* smaller font */
    display: inline !important; /* to get on same line as title */
}

.thread-posting .header .author br {
    display: none !important; /* 'edited' note on same line */
}

.thread-posting .header .author .edited:before {
    content: "// " !important; /* visually separate from @reply_name */
}

.thread-posting .header .author strong a {
    font-weight: normal !important; /* unbold author name/link */
}

.thread-posting .header .author .admin {
    font-weight: bold !important; /* but leave admins bold */
}


/***************************
 ** main part of the post **
 ***************************/
.thread-posting .wrapper .body {
    padding: 3px 0px 0px 10px  !important; /* reduce padding of thread body */
}

.thread-posting .wrapper .body p {
    margin-bottom: 10px !important; /* reduce space after paragraph */
    max-width: none !important; /* wrap text at edge of post box, not after 60em */
    padding-right: 10px !important; /* keep text from bumping edge of post box */
}

.thread-posting .wrapper .body blockquote {
    margin: 4px 0px 0px 4px  !important; /* reduce margin */
    padding: 0px 0px 0px 6px !important; /* reduce padding */
    background: none !important; /* get rid of quatation marks image */
    border-left: solid !important; /* replace quote marks with solid line */
    border-width: 2px !important; /* size of the line */
}


/************************
 ** footer of the post **
 ************************/
.posting-footer {
    margin: 0px 10px 0px 10px  !important; /* reduce margin */
    padding: 2px 0px 0px 0px !important; /* pad away from top border */
    border-top: dotted !important; /* line to separate from post body */
    border-width: 1px !important; /* very small line */
    border-color: lightgrey !important; /* make line less obtrusive */
    
}

.posting-footer .reply {
    font-size: 0.7em !important; /* smaller font */
}

.posting-footer .reply a.stronglink {
    background: none !important; /* get rid of reply arrow */
    font-weight: normal !important; /* unbold reply link */
    padding: 0px !important; /* remove unnecessary padding */
}


.posting-footer .info .views {
    font-size: 0.7em !important; /* bigger font (from 0.69 lol) */
    float: right !important; /* raise view counter to line up with reply link */
}

.posting-footer .info .options {
    font-size: 0.7em !important; /* bigger font */
    margin: 0px 10px 0px 0px !important; /* move edit/delete away from view counter */
    float: right !important; /* alignment purposes */
    line-height: 1.2em !important; /* alignment purposes */
}


/*******************************
 ** outisde of the post block **
 *******************************/
.thread-posting {
    margin: 0px 0px 5px 0px !important; /* reduce space between posts */
}

.reply-wrapper {
    border-left: solid !important; /* add post indentation lines on left */
    border-width: 1px !important; /* keep them thin */
    border-color: rgb(210, 221, 234) !important; /* same as 'read post' color */
}
}
