css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

#header, #footer,
.navigation_link
{
background: -moz-linear-gradient(#333333, #000000)!important;
}
#navigation, #nav-view, 
html, body, #root, #nav-view, #navigation
{
background-color: #333333 !important;
}
#search_input, input, textarea
{
background: #111111 !important;
}
.style_card, .advancedsettings_shown, #notes_textarea,
#left_information, #center_information, #right_information
{
background-color: #000000 !important;
}
#header
{
border-bottom: 1px #000000 solid !important;
}
#input_text_search
{
border: 1px #111111 solid !important;
}
input, textarea
{
border: 1px #111111 solid !important;
}
.navigation_link_active
{
border-left: 3px #0000FF solid !important;
}
#header, .linksBox .navigation_link:last-child,
.style_card, #single_discussion
{
box-shadow: none !important;
}
.category_title
{
font-size: 16px !important;
}
#information_value
{
text-align: center !important;
}
#left_information, #center_information, #right_information
{
padding: 10px !important;
}
#update_style_button
{
color: #FFFFFF !important;
background: -moz-linear-gradient(#FFA500, #000000)!important;
border: 1px #FFA500 solid !important;
}
#style_installed_button
{
color: #FFFFFF !important;
background: -moz-linear-gradient(#00FF00, #000000)!important;
border: 1px #00FF00 solid !important;
}
#install_style_button
{
color: #FFFFFF !important;
background: -moz-linear-gradient(#FF0000, #000000)!important;
border: 1px #FF0000 solid !important;
}
#create_new_style, .div-arrow, .current, .view_details,
.css_button, .advanced_button, .white_button, #css_button, #send_feedback_button, .see_more
{
color: #FFFFFF !important;
background: -moz-linear-gradient(#0000FF, #000000)!important;
border: 1px #0000FF solid !important;
}
a:hover, .navigation_title:hover,
#create_new_style a span:hover,
.ellipsis a:hover
{
color: #FFF000 !important;
}
a, h4 a, body, input, textarea, .style_author,
.navigation_title, .style_updated,
.category_title, #stylish-description,
.by-author, .author-name a, #share_div, #notes_textarea,
#information_key_left, #infomation_value_left,
#headline, #applies_link, .text_style_page,
#information_key, #information_value, #title_div h1,
#create_new_style a span, .navigation_category,
.title_setting, .style_description_long,
.style_author_long, .style_info_long #title_long a
{
color: #FFFFFF !important;
}
}

@-moz-document domain("forum.userstyles.org") {

#Head, .Button
{
background: -moz-linear-gradient(#333333, #000000)!important;
}
#Body
{
background-color: #333333 !important;
}
.Tag
{
background-color: #FF0000 !important;
}
#Frame, textarea, .Item.Read, code, pre
{
background-color: #000000 !important;
}
.UserQuote
{
border-top: 2px #000000 solid !important;
border-left: 4px #000000 solid !important;
border-right: 2px #000000 solid !important;
border-bottom: 2px #000000 solid !important;
}
#Panel .FilterMenu li, .PanelInfo li, .Item 
{
border-bottom: 2px #000000 solid !important;
}
.Button, code, pre
{
border: 1px #000000 solid !important;
}
.Button
{
box-shadow: none !important;
}
textarea, .ViewCount, .CommentCount, .LastCommentBy,
.LastCommentDate, .ShowDiscussionAuthor, .DateUpdated,
label
{
color: #FFFFFF !important;
}
a:hover
{
color: #000000 !important;
}
.Item.Read .Title a:hover
{
color: #FF0000 !important;
}
}
