#logo {height: 0 !important;



	width: 0 !important;



	padding-left: 251px !important;



	padding-top: 46px !important;



	background: url(http://i.imgur.com/8gnLQ.png) no-repeat !important;}





@namespace url(http://www.w3.org/1999/xhtml);@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {body {background: url('http://static.tumblr.com/k38ic7e/q5Smij17p/ejryjyj222222.jpg') center #000080 no-repeat fixed !important;}img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{opacity:0.00 !important;}#new_post{background-color:rgba(255,255,255,0.25)!important;}.new_post_label,#tags .header DIV A{color:rgba(0,0,0,1.0)!important;}#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: -moz-linear-gradient(left,#B00000,#802A00,#808000,#008000,#000080,#00002A,#2A002A)!important;background:rgba(0,0,0,0.25)!important;-moz-box-shadow: 1px 2px 3px #000000 !important;border-radius:12px!important;}.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){background:none!important;width:0px!important;height:0px!important;margin-top:0px!important;border-bottom:solid 11px rgba(0,0,0,0.25)!important;border-right:11px solid transparent!important;border-left: 11px solid transparent!important;}.selection_nipple:not([class="selection_nipple white"]){border-bottom:solid 11px rgba(0,0,0,0.25)!important;}.mceContentBody{color:rgba(255,255,255,1.0)!important;}

#right_column .controls_section .recessed {
background-color: #000;
opacity:0.6;
-webkit-box-shadow: none;
-moz-box-shadow: none;
box-shadow: none;
-webkit-border-bottom-right-radius: 6px;
-webkit-border-bottom-left-radius: 6px;
-moz-border-radius-bottomright: 6px;
-moz-border-radius-bottomleft: 6px;
border-bottom-right-radius: 6px;
border-bottom-left-radius: 6px;
position: relative;

}

#right_column .controls_section .recessed:hover {
background-color: #000;
opacity:0.6;
-webkit-box-shadow: 0 0 5px #000;
-moz-box-shadow: 0 0 5px #000;
box-shadow: 0 0 5px #000;
}

#right_column .controls_section .selected {
background: #008000;
background: -moz-linear-gradient(top,#008000 0,#008000 50%,#008000 50%,#008000 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#008000),color-stop(50%,#008000),color-stop(50%,#008000),color-stop(100%,#008000));
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#008000',endColorstr='#008000',GradientType=0);
-webkit-box-shadow: 0 0 5px #008000,inset 0 1px 0 rgba(255,255,255,.40);
-moz-box-shadow: 0 0 5px #008000,inset 0 1px 0 rgba(255,255,255,.40);
box-shadow: 0 0 5px #008000,inset 0 1px 0 rgba(255,255,255,.40);
-webkit-border-radius: 1px;
-moz-border-radius: 1px;
border-radius: 1px;
border-top: none;
}

#right_column .controls_section .selected:hover {
background: #008000;
background: -moz-linear-gradient(top,#008000 0,#008000 50%,#008000 50%,#008000 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#008000),color-stop(50%,#008000),color-stop(50%,#008000),color-stop(100%,#008000));
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#c31c16',endColorstr='#008000',GradientType=0);
-webkit-box-shadow: 0 0 5px #008000;
-moz-box-shadow: 0 0 5px #008000;
box-shadow: 0 0 5px #008000;

border-top: none;
}

#right_column .radar .content {
background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png') repeat-y;
position: relative;
}

#right_column .radar .content.is_media .mask.top {
height: 9px;
background: none;
background-position: 0 0;
top: 0;
}

#right_column .radar .content.is_media .mask.bottom {
height: 19px;
background: none;
background-position: 0 -9px;
bottom: 0;
}

#content_top{width:0px!important;height:0px!important;}#content_bottom{width:0px!important;height:0px!important;}#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding-top:25px!important;padding-bottom:25px!important; -moz-border-radius: 20px !important;}LABEL {color:rgba(0,0,0,1.0)!important;}#footer{display: none !important;}.post.link.not_mine:hover,.post.quote.not_mine:hover,.post.audio.not_mine:hover,.post.video.not_mine:hover,.post.regular.not_mine:hover,.post.note.not_mine:hover,.post.photo.not_mine:hover{border-top-right-radius: 24px !important;opacity:1!important;}.permalink{background:none!important;width:16px!important;height:16px!important;background:rgba(170,179,188, .85)!important;border-bottom-left-radius:10px !important;border-top-right-radius:24px !important;margin-top:6px !important;margin-right:6px!important;}.permalink:hover{background:rgba(127,139,152,1) !important;}.selected .following,.selected .followers,.selected .settings,.selected .posts,.selected .messages,.selected .drafts,.selected .queue,.selected .likes{color:rgba(255,255,255,0.50)!important;background:rgba(44,71,98, 1.0)!important;}#new_post .arrow{opacity:.25!important;margin-left:-1px !important;}#categories .category,#categories .category.active{color:rgba(255,255,255,.85)!important;}#dashboard_directory #tabs, #main_explore #wire{margin-top:-25px !important;}#new_post_label_text,#new_post_label_photo,#new_post_label_quote,#new_post_label_link,#new_post_label_chat,#new_post_label_audio,#new_post_label_video{font: normal .85em verdana !important;text-transform:lowercase!important;} li.selected, li.recessed, a.active, div.post_filter_tabs a {
background: none !important}#search_query:hover,.right_column_section li a:hover,#right_column UL:first-child LI:first-child A:hover,#right_column UL:nth-child(3) LI:first-child A:hover{background:rgba(255,255,255,.25)!important;color:white!important;}.post.photo_post .photo,.post.photo_post .video_thumbnail{-moz-box-shadow: 1px 1px 3px #000000 !important;border-radius:12px!important;}.content.is_media,.mask.top,.mask.bottom{background:none!important;}div#right_column,div#right_column:after{background: none !important;}}

@-moz-document domain("tumblr.com") {

.new_post_label .new_post_label_icon
{background-image: url('http://i.imgur.com/dK7zh.png') !important;
}
}
