css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.amazon.de/gp/css/order-history") {
@media print {
* {color:black !important;}
.order-details li:nth-of-type(1), 
.order-details li:nth-of-type(2),
.ship-contain div:last-child,
.order-details .info-title,
#cs-orders .action-bar:last-child, /*removes pagination*/
.order-level .top-text, .order-level .order-links, .item-action-button, .status, .page-top, .self-service, header {display:none !important;}
.order-bar {min-height:0 !important;margin-left:190px !important;}
.de-ss-margin {margin-right: 0 !important;}
.ship-contain .shipment {padding: 0 !important;}
.price {font-size:1.5em !important; float:right;}
.order-level {width: auto !important; max-width:190px;}
ul.order-details { margin: 0 !important; padding: 0 !important;}
ul.order-details li {line-height:2.5em !important; float:left;}
.shipment li + li {border-top: 1px #ddd solid; margin-top:.5em !important; padding-top:.5em !important;}
#MinWidthContainer #PageContainer #PageContentContainer {max-width: 100% !important;}

}
}
