css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.zeit.de/'), url-prefix('http://blog.zeit.de/') {
body {background-color:#666 !important; overflow-x: hidden !important;}
#wrapper {background-color:#fff !important; padding-left:15px !important; padding-right:15px !important; margin-top:40px !important; border:6px outset #999 !important; border-bottom:none !important;}
#content {background:none !important;}
div#main {width:880px !important;}
div#main.article {-moz-column-count:2;}
div#main.article img {-moz-column-span:all !important; float:left !important; margin: 0 10px 10px 0; max-width: 100% !important;}
div#main.article p { line-height:1.3em !important; text-align: justify !important; -moz-hyphens: auto; hyphens:auto;}
div#main.article p:last-child {margin-bottom:0 !important;}
ul.teaserlist li.lead ul.doublelead {background:transparent !important;}
.logo {height:45px !important;}
.narrow {width:auto !important; margin: 0 !important; padding: 0 !important;}
#main .lead .supertitle, #main .doublelead {margin:0 !important;}
li.lead ul.shortteaserlist {clear:none !important;}
ul.doublelead ul.shortteaserlist, ul.doublelead h3.supertitle {display:none !important;}
.lead a img {float:left !important; padding: 0 10px 10px 0 !important;}
.article img[width="540"] {width:430px !important;}
ul.shortteaserlist li, ul.shortteaserlist li div, ul.shortteaserlist {padding: 0 !important; display:inline;}
ul.shortteaserlist li {font-size:10px !important; line-height:10px !important;}
ul.shortteaserlist li strong {color:inherit !important; font-size: inherit !important; text-transform: none !important;}
ul.shortteaserlist li:before {content:"- ";}
ul.shortteaserlist li.first:before {content:"";}

body.body-gallery {color:#666 !important;}
body.body-gallery #header {display:none;}
body.body-gallery #wrapper {width: 950px !important; background-color:transparent !important; border:none !important;}
body.body-gallery {background-color:#222 !important;}
body.body-gallery img#gal_image {width:100%;}
body.body-gallery #gal_container {overflow:visible !important;}
body.body-gallery div#main.article {-moz-column-count:1;}
.bigaimage .caption {color: #666 !important;}

.mainnav, #header, .center {width:900px !important;}
ul.mainnav li {margin:0 !important;letter-spacing:.3px;}
.innerteaser {width:auto !important; float:none !important;}
#header form, #header .zeitonline {top:10px !important;}
.excerpt {font-size: 16px !important; line-height: 24px !important;}
#smk-plug,
.tracker,
a.more-link,.inline.link,
#ugc,
.toolad, .monsterbanner, .sponsoring,
.infobox,.copyright,.pagination,
.article h1.supertitle,
.ressortbacklink,
.commentcount,

.inline.portrait,
#header .nav,
#header .cap,
.userlogin,
#bottom,#top,
.stocks, .weather, #comments, .zol_video,
#services, #teasermosaic, #informatives,
ul.topnav3,

ul.mainnav>li>a[href$="zeit.de/index"],
ul.mainnav>li>a[href$="gesellschaft/index"],
ul.mainnav>li>a[href$="kultur/index"],
ul.mainnav>li>a[href$="karriere/index"],
ul.mainnav>li>a[href$="reisen/index"],
ul.mainnav>li>a[href$="auto/index"],
ul.mainnav>li>a[href$="sport/index"],
ul.mainnav>li>a[href$="lebensart/index"],

#informatives ul.teaserlist,
.ad
{display:none !important;}

}
