css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("taz.de") {
span#headerleft {width:870px !important;}
ul#newsnavigation ul {width: auto !important; top: 0px !important; left:auto !important; right: 0px !important; text-align:right !important; padding:0 !important;}
div#seite {margin-top:66px !important; margin-bottom:0 !important;}
span#hauptspalte {width:100% !important; margin-bottom:0 !important;}
div.sect h4 {display:inline; float:right; top:-2px !important; text-transform:none !important; margin-bottom:0 !important; padding-bottom:0 !important;}
div.sect_adr {display:block; border-top: 6px black solid; border-left: 6px black solid; padding:5px 0 0 4px;}
div.sect h3 {display:block;}
div.sect_adr ul.sectbody {border:none !important;}
ul.sectbody li a {padding-top:2px !important;}
ul.sectbody li a h3 {padding-bottom:2px !important;}
ul#newsnavigation, a[href]:hover h3, p a[href]:hover {text-decoration:none !important; cursor:pointer !important;}
.sectbody {border:0 !important;}
.sect_leads .sectbody a.pictured, .sect_adr .sectbody a.pictured {min-height:0 !important; padding-bottom:10px !important;}
div.sect_adr p {padding-bottom:10px !important;}
a[href$=".jpg"] {float:left; padding-right:8px;}
p.caption {float:left; max-width:460px; padding:0 8px 0 0 !important;}
.sect_adr .sectbody li a {border-top-color:#CCCCCC !important;}

.sticker,
#footer ul,
#globalnavigation a,
.sectfoot .share h6,
.sectfoot .service,
div.rack,
span.author,
div.sect_seealso,
div.sect_article div.secthead,
ul.uplinks,
div.sect_commentlinks.box,
div.sect_fav.box,
li.sectbody_first h4,
div.sectfoot,
div.sect_tdt,
div.sect_wf,
div.sect_spb,
div#footer div[class],
ul#newsnavigation li a[href$="nord/"],
ul#newsnavigation li a[href$="berlin/"],
ul#newsnavigation li a[href$="sport/"],
ul#newsnavigation li a[href$="wahrheit/"],
ul#newsnavigation li a[href$="debatte/"],
div#adition_zone_banner,
ul#globalnavigation li:not(:first-child),
span#headerright,
span#marginalspalte
{display:none !important;}

}


@-moz-document url("http://www.taz.de/") {

span#hauptspalte > div:nth-last-child(1), /*Reise*/
span#hauptspalte > div:nth-last-child(2), /*Nord*/
span#hauptspalte > div:nth-last-child(3), /*Berlin*/
span#hauptspalte > div:nth-last-child(4), /*Sonntazwette*/
span#hauptspalte > div:nth-last-child(5), /*Wahrheit*/
span#hauptspalte > div:nth-last-child(8), /*Sport*/
span#hauptspalte > div:nth-last-child(10) /*Debatte*/
{display:none !important;}
}
