css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("imgur.com") {
.thumbs-carousel {display:none !important;}
}
@-moz-document domain("tumblr.com") {
.overlay, noscript, .postInfo, #header, #tumblr_controls {display:none !important;}
}
@-moz-document domain("facebook.com") {
.uiStreamStory .photoRedesignAspect:after, .uiStreamStory .photoRedesignCover:after, .uiStreamStory .photoRedesignLink .uiScaledImageContainer:after {
content: none !important;}
.translate_story_link, .commentBody div[class^="mts comment_translate"] {display:none !important;}
}
@-moz-document domain("adsoftheworld.com") {
#comments, body, #content, #header .primary, #primary {background:white !important; border:none !important;}
#primary .frame {background-color:#222 !important;}
#comments {padding-top:0 !important;}
.frame {padding: 30px !important;}
.frame img {width:auto !important;height:auto !important;max-width:1100px !important;max-height:750px !important;}
#header .primary {height:auto !important;}
/*$div > span em.term-4,*/
.player-switch, #comments h2,
.comment .fivestar-widget-static, .fivestar-widget, .avatar, .link-0, .details p:last-child, #options, #nodePager, .secondary,
#secondary, #footer, #block-menu-menu-all-creative-world, #branding, #navigation {display:none !important;}
}
@-moz-document domain("deviantart.com") {
.reportit, #favebtn-wrap, #gmi-ResourceViewPrintButton,
.deviation-share, .ch-boxtop, #deviation_critiques, .icon.i3, 
#browseNavbar2 .navbarLeft, .tt-w small, .browse2-feeds, #see-also,
img#gmi-ResViewSizer_img, div.gr-body .gr .hh table, div.gr-body .gr .hh h1,
div.gr-body .gr .cc-name, div[class*="ad-block"], #overhead-collect,
.browse-limits-h.bt-selector, .sitbackbutton.bt-selector, #search7,
#mcstatus, .friendsmenu, .collectmenu, #gmi-CCommeXXntMaster, #overhead-promo,
#depths, .h.left-sidebar, #facetMenu-Browse, #overhead .oh-mmain, #overhead .oh-bl,
.squareBrowsead, .group_featured_list,
.commons_license_summary {display:none !important;}
body {padding-top: 0 !important;}
img#gmi-ResViewSizer_fullimg, img#gmi-ResViewSizer_fullimg:hover {display:block !important;}
#gmi-ResourcePageMetaPane {opacity:0 !important; position: absolute !important; z-index:10001; right:-300px; top:0; -moz-transition: all 400ms ease-in-out;}
#gmi-ResourcePageMetaPane:hover {opacity:.8 !important; width:auto !important;right:0 !important; background-color:#CCD9C8; -moz-transition: none;}
body{overflow-x:hidden;}
#gmi-ResViewSizer_fullimg[rs_src]:after {
background: attr(rs_src, url) no-repeat scroll 0 0 transparent;
color: rgba(0,0,0,.2); content: attr(rs_src); min-height: 100%; min-width: 100%; position: absolute; top: 0; left: 0; -moz-user-select:text;
}
}

@-moz-document url("http://www.flickr.com/explore/interesting/7days/"), url("https://secure.flickr.com/explore/interesting/7days/") {
body {background-color:#222 !important;}
.TwentyFour {margin-top: 20px !important; margin-bottom: 0 !important;}
.Photo {text-align:center !important;}
noscript iframe, br#MainFooterClear, #Main form, #Tertiary, tr[valign="top"], #int-rand-txt, #TopBar, #foot {display:none !important;}
}

@-moz-document domain("www.flickr.com"), domain("secure.flickr.com") {
noscript, p.ResultsThumbsChildMedium, p.ResultsThumbsChild, #PivotsContainer, #TopBar, #foot, .spaceball, #photo-drag-proxy {display:none !important;}
}

@-moz-document domain("sxc.hu") {
#istock_thumbs_1, #istock_thumbs_2, .bann, #textlinks, #footer, #header, #sxpt2_1, #sxpt2_2, #istock_thumbs, #sxpt2 {display:none !important;}
}

@-moz-document url-prefix("http://www.boston.com/bigpicture/") {
#sharetoolContainer, #elsew, #bpTools, #footer, #rnav, #moreLinks, 
table.tableTop, .bottomFrontPage, #outBrain, .commentDivide {display: none !important;}
#content > div, body {background-color: #111 !important;}
.bpBody {color:#fff !important;}
#mastHead, #globalNav, #blogHeader {opacity:0 !important;}
}

@-moz-document domain("9gag.com") {
.feature-bar, .post-may-like, #post-control-bar, #view-controller, #footer-back-to-top, .side-dock, #headbar-wrap, #footer,
.sticky-items h4, .sticky-items p, .sticky-items ul, .sticky-items >div>div {display: none !important;}
#container {margin-top:10px !important;}
#entry-list.list ul.col-1 li {padding: 40px 0 0 0 !important;}
#main {margin:0 auto !important; width: 100% !important;}
:focus {color: #c33 !important;}
}

@-moz-document domain("www.pixelio.de") {
br, #login_box, #footer, #partner, #navigation, #header, #clipdealer {display:none !important;}
body, div#main_content {width: 100% !important; margin:0 !important; padding:0 !important;}
.boxProfile {width: 1200px !important;}
}
