@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("10gag.co.il") {
#Gag-Content
{
opacity: 0;
    animation: anim ease-in 1;
    animation-fill-mode: forwards;
    animation-duration: 0.5s;
}
@keyframes anim

	{

from {opacity: 0;}

to {opacity: 1;}
	}

.Followed-Users li .Followed-User-Avatar {
    opacity: 0.5 !important;
}
.Followed-Users li .Followed-User-Avatar:hover {
    box-shadow: 0 0 25px #000;
    z-index: 30;
    opacity: 1 !important;
}
input:focus
{ 
	box-shadow:0 0 10px #F7941D !important;
}
  #ContentPlaceHolder1_ContentPlaceHolder1_tags_tag
  {
  Border-radius: 10px;
  }
  .tag
  {
  Border-radius: 10px !important;
  }
  .logo
  {
  transform: scale(0.9);
  }
    .logo:hover
  {
  transform: scale(1.0) !important;
  }
  div.Chat-Input-Holder
  {
      background: -moz-linear-gradient(center top , blue, cyan) repeat scroll 0 0 transparent;
  }
  #messagesList time {
    color: #008AFF !important;
}
.userTypeAdmin
{
color: pink !important;
}
div.rightPanel
{
    border-left: 1px dotted blue !important;
}
#messagesList {
    color: #fff;
}
 div.Chat-Input-Holder {
    background: -moz-linear-gradient(center top , blue, cyan) repeat scroll 0 0 transparent !important;
}
#sendChat {
    background: -moz-linear-gradient(center top , #292929, #000) repeat scroll 0 0 transparent !important;
}
#sendChat:hover {
    background: -moz-linear-gradient(center top , #000, #292929) repeat scroll 0 0 transparent !important;
    border: 1px solid #000;
}
