css:
@-moz-document url-prefix("http://www.canalplus.fr/"), url-prefix("http://canalplus.fr/") {
body {
  background: rgb(0, 0, 0);
  background-image: none !important;
}
div#canalplus #content {
background: rgb(1, 1, 1);
_background: #fff !important;
overflow: auto;
min-height: 300px;
_height: 1%;
_width: 1000px;
}
#habillage {
  display:none;
}
#breadcrumb {
background: rgb(0, 0, 0);
}
div.breadcrumb {
background-color: rgb(58, 58, 58);
}
div#rightSection.rightSectionGrey {
background-color: rgb(0, 0, 0);
padding: 0 15px;
width: 300px;
float: right!important;
margin: 0;
}
div.feature h4 a {
color: rgb(182, 15, 0);
}
ul.features li p {
color: rgb(177, 177, 177);
margin: 0;
background-color: rgba(5, 5, 5, 0);
}
div#canalplus ul.thumbs-live li {
background-image: none;
  background:#333;
color:#444;
}
ul.features-alt-videov3-2x4 li h4 a {
color: rgb(184, 184, 184);
}
div#canalplus ul.thumbs li h4 a {
color: rgb(206, 206, 206);
}
div#mainSection div#livePush {
background-color: rgb(1, 1, 1);
}
div#mainSection div#livePush div.push-content, div#mainSection div#livePush div.bg-top-blockPush
{
background-image: none;
  background-color: rgba(255, 255, 255, 0);
}
div.push-content-Tab ul.tabs, .tabs li {
background-image: none !important;
}
._tabs li {
background-image: none !important;
  background-color:rgba(250,250,250,0.5);
}
ul.tabs li a {
 background-color:rgba(250,250,250,0.1);
color: #fff;
    background-image: none !important;
}
div.push-content-Tab ul.tabs li a span {
 background-color:rgba(250,250,250,0);
color: #555;
    background-image: none !important;
}
  div.push-content-Tab ul.tabs li.current a span {
 background-color:rgba(250,250,250,0.1);
color: #FFF;
}
div.facebook_fan_box {
opacity:0.05;
}
div.facebook_fan_box:hover {
opacity:0.6;
}
div#rightSection.rightSectionGrey div.col.rightLive .btn span, div#mainSection div.videoLive-content-tab div#contenuOnglet a.btn-selected span, div#mainSection div.videoLive-content-tab div#contenuOnglet a.btn-selected, .btn, .btn span {
background-image: none;
 background-color:rgba(250,250,250,0.1);
}
div#content div.vitrine_relance {
background-color: rgb(0, 0, 0);
border-top: 1px solid #ccc;
}
div#content div.vitrine_relance div.cols div.col div.feature {
background-color: rgb(39, 39, 39);
border-right: 1px solid rgba(53, 53, 53, 0);
border-left: 1px solid rgba(217, 217, 217, 0);

}
 div#content div.vitrine_relance div.cols div.col div.bottomBlockRelance, ul.videos li 
{
background-image: none;
}
ul.videos li 
{
background-color:#111;
      color:#444;
}
div.videos-bar {
border-top: 1px solid #d9d9d9;
background: rgb(0, 0, 0);

}
#canalplus_footer, .videos-bar, .vitrine_relance, vitrine_relance_live, #rightSection, #contenuOnglet, #canalplus_header, div#rightSection.rightSectionGrey div.vitrineExtrude {
opacity: 0.2;
}
#canalplus_footer:hover, .videos-bar:hover, .vitrine_relance:hover, vitrine_relance_live:hover,#rightSection:hover, #contenuOnglet:hover, #canalplus_header:hover, div#rightSection.rightSectionGrey div.vitrineExtrude:hover {
opacity: 1;
}
div.textLeftFirst {
color: rgb(162, 162, 162);
border-right: 1px solid #ccc;
}
}
