css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("blog.us.playstation.com") {

/*
 * Blog.us.playstation.com - Bright background
 *
 * Author      : DaftMav
 * Updated     : February 18th, 2010
 * Description : A simple bright version for the Official US Playstation blog. For those that have difficulty reading text on dark backgrounds.
 * Usage       : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

body {
 background-color: #ddd !important;
}
div.page, div.avatar, div.content {
 background-color: white !important;
}
div.sidebar, div.subscribe, div.recent, div.post, div.border, div.featured-post {
 background-color: #eee !important;
 color: black !important;
}
div.date-box {
 background-color: #eee !important;
 background-image: none !important;
 width: 28px !important;
 border: 5px solid #5d5d5d !important;
}
div.date-box p {
 padding: 4px 0 3px 0 !important;
}
div.date-box p span {
 background-color: #eee !important;
 padding: 0 1px !important;
}
div.sidebar, div.border {
 background-image: none !important;
}
.left, p {
 color:black !important;
}
blockquote {
 background-color: #DDDDDD !important;
}
div.alignleft a {
 background-color: #5D5D5D !important;
 color: white !important;
}
h2 {
border-bottom: 2px solid #5b5b5b !important;
}
ul, ul li, ul li a, div.list a, ul li a span, span, div.subscribe h5, div.recent-posts ul li a:hover {
 color: black !important;
 background-color: transparent !important;
}
ul li a:hover, div.list a:hover {
 color: white !important;
}
div.twitter ul li a:hover, div.recent-posts ul li a:hover span {
 color: #4A8285 !important;
}
a.delicious-link, a {
 color: #4A8285 !important;
}
a.delicious-link:hover, a:hover {
 color: #4A8285 !important;
}
div.downloads a:hover{
 color: #4A8285 !important;
}
fieldset select {
 background-color: #ddd !important;
}
fieldset select option{
 background-color: #ccc !important;
 color: black !important;
}

/* special replies */
div.author-comment{
 background-image: none !important;
 background-color: #900100 !important;
 padding: 10px !important;
}
div.author-comment h1 {
color: #eee !important;
}
div.author-comment p {
 background-color: #eee !important;
 padding: 3px !important;
}
div.author-comment div.avatar {
margin:0 !important;
border:0 !important;
border-bottom:6px solid #900100 !important;
border-left:6px solid #900100 !important;
}

}
