css:
@-moz-document url-prefix('https://www.tumblr.com/dashboard'),
url-prefix('https://www.tumblr.com/inbox'),
url-prefix('https://www.tumblr.com/settings'),
url-prefix('https://www.tumblr.com/likes'),
url-prefix('https://www.tumblr.com/blog'),
url-prefix('https://www.tumblr.com/followers'),
url-prefix('https://www.tumblr.com/reblog'),
url-prefix('https://www.tumblr.com/new'),
url-prefix('https://www.tumblr.com/liked'),
url-prefix('https://www.tumblr.com/edit'),
url-prefix('https://www.tumblr.com/tagged'),
url-prefix('http://www.tumblr.com/dashboard'),
url-prefix('http://www.tumblr.com/inbox'),
url-prefix('http://www.tumblr.com/settings'),
url-prefix('http://www.tumblr.com/likes'),
url-prefix('http://www.tumblr.com/blog'),
url-prefix('http://www.tumblr.com/followers'),
url-prefix('http://www.tumblr.com/reblog'),
url-prefix('http://www.tumblr.com/new'),
url-prefix('http://www.tumblr.com/liked'),
url-prefix('http://www.tumblr.com/edit'),
url-prefix('http://www.tumblr.com/tagged') 
{
    body
    {
        background-image:url('http://i.imgur.com/OVHIP8p.png?1') !important;
        background-attachment:fixed !important;
        background-repeat:no-repeat !important;
    }

    *{color:  #4e4d4d !important;} 
    a:hover {color:#000000 !important;}
    a {color:#4e4d4d !important;}
    
    
    .permalink {
        background: none !important;  
        background: #cbc9c3 !important; 
        width: 16px !important; 
        height: 16px !important; 
        border-bottom-left-radius: 10px !important; 
        border-top-right-radius: 10px !important; 
        margin-top: 3px !important; 
        margin-right: 4px !important;
    } 
    
    a.permalink {
        background-color:#666e77 !important; 
        opacity:.6;  
        background-image:none !important; 
        border:1px !important;
    } 

    .selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
    	background: none !important; 
    	width: 0px !important;
    	height: 0px !important; 
    	margin-top: 0px !important; 
    	border-bottom: solid 11px white !important; 
    	border-right: 11px solid transparent !important; 
    	border-left: 11px solid transparent !important; 
	} 
	
    .selection_nipple:not([class="selection_nipple white"]) {
        border-bottom: solid 10px #666e77 !important;}

    #logo {
    	height: 0 !important;
    	width: 0 !important;
    	padding-left: 240px !important;
    	padding-top: 145px !important;
    	background: url('http://i.imgur.com/92sKswg.png') no-repeat !important;
    }
    
    .search_form_row {background: rgba(0,0,0,0.2) !important;}
    
    #post_controls {background:none !important;}
    
    #dashboard_inbox div.l-content,   
    #clearfix {border: 1px solid !important; background: none !important;}
    
    .l-content {background: rgba(0,0,0,0.2) !important;}   
    
    /*remove blue and white bits*/
    #right_column:after, 
    #right_column,
    #left_column,
    .post_content, 
    .post_tags:after, 
    .section.first.divider,
    blockquote pre,
    #tumblr_radar {
    background:none !important;
    }
    
    #dashboard_index, 
    #dashboard_edit_post, 
    #dashboard_submissions, 
    #dashboard_drafts,
    #dashboard_post_queue, 
    #dashboard_followers, 
    #dashboard_members{background-color:#FFFFFF; }
    #dashboard_index div.l-content, 
    #dashboard_submissions div.l-content, 
    #dashboard_drafts div.l-content,
    #dashboard_post_queue div.l-content, 
    #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(0,0,0,0.2) !important;}
    #dashboard_edit_post{background-color: #000000 !important}
    #right_column .dashboard_nav_item {border-bottom:0px solid #eee !important;background-image:none !important}
    #right_column .dashboard_nav_item .dashboard_nav_title, 
    #right_column .dashboard_nav_item .dashboard_nav_title a,
    #right_column .dashboard_nav_item a,#right_column .dashboard_nav_item ul.dashboard_subpages,
    #right_column .dashboard_nav_item ul.dashboard_subpages li a {color:#FFF !important;}
    #right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {color:#888 !important ;}
    #right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {background: none !important;}
    #next_page_link {background: none !important;}
    #previous_page_link {background: none !important;}       
    #left_column #posts .notification .avatar {border-color:transparent !important;background-color:#000000 !important;opacity:0.5 !important;}
    #left_column #posts .notification .avatar:hover{opacity:100 !important;}.dashboard_switch_blog_menu_header{background-color:#000000 !important;color:#000 !important;}.dashboard_switch_blog_menu_item

    
    #popover_blogs .tab_notice:after {display:none;}
    div.l-header .tab.iconic > a {background-image:url('http://i.imgur.com/1jXD2fq.png') !important;}

    .new_post_label .new_post_label_icon {
    background-image: url('http://i.imgur.com/r5lsHLO.png') !important;
    margin-top: 20px !important;
    height: 94px !important;
    width: 80px !important;
    opacity: .9 !important;
    background-size: 520px 190px !important;
    }

    #posts .post.new_post {
    background-image: url('http://i.imgur.com/Y1TCFI9.png'), url('http://i.imgur.com/9qega3h.png') !important;
    background-position: top center !important;
    background-repeat: no-repeat, repeat !important;
    }

    #posts .post.new_post .arrow {
    background: transparent url('http://data.sainet.co.uk/medigel/nipple/nipple_nightsky.png') !important;
    background-repeat: no-repeat !important;
    }

    #new_post .cog {
    margin-right: -15px !important;
    }

    div.l-content #new_post .new_post_label {
    color: rgba(226,224,209,0) !important;
    line-height: 3.9em;
    font-weight: bold !important;
    font-size: .85em !important;
    }

    #new_post .main_content {
    background: rgba(255,255,255,0.95) !important;
    color: rgba(0,0,0,0.7) !important;
    border: none !important;
    }
    #new_post .popover_inner.clearfix, #new_post .post_options .popover_inner, #tag_suggest.popover, .post_options.popover {
    background: rgba(255,255,255,0.95) !important;
    }
}
