css:
/* 黒翼猫のコンピュータ日記 2nd Edition ja jp liquid compact 2014/12 */
@-moz-document url-prefix("http://blog.livedoor.jp/blackwingcat/"){
/*======================================
  黒翼猫のコンピュータ日記 2nd Edition 2014/12
  ======================================*/
/*--- unwanteds ---*/
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: medium !important; font-weight: normal !important;
 line-height: 1.4em !important;
 }
#container {width: auto !important;}
#blogcontainer {width: auto !important; padding: 0 !important;}

/*--- header ---*/
#banner {width: auto !important;}
#banner {background-position: 0 0 !important;}
#banner {background-color: #262A2A;}
.blogtitle {padding: 0 !important; text-align: center;}
.description {padding: 0 !important;}
.description h4 {margin: 0 !important; text-indent: 1em;}

/*--- footer ---*/
#footer, #outfooter {display: none !important;}

/*--- main contents ---*/
#wrapper, #right {position: relative !important;}
#wrapper {width: 100% !important; margin: 0 -130pt 0 0 !important;}
#right {width: 130pt !important; margin: 0 !important; padding: 0 !important;}
#right * {max-width: 130pt !important;}
#wrapper>#content, #wrapper>#left {position: relative !important;}
#wrapper>#content {width: 100% !important; margin: 0 0 0 -140pt !important;}
#wrapper>#content>#blog {width: auto !important; margin: 0 130pt 0 140pt !important;}
#wrapper>#content>* {padding: 0 0.5em 0 0.5em;}
#wrapper>#left {width: 140pt !important; float: left; padding: 0 !important;}
#wrapper>#left * {max-width: 140pt !important;}
}

/*================================================*/
/* @sectionname: ティルさんの気まぐれ日記 Season II */
/*================================================*/
@-moz-document url-prefix("http://thyrving.livedoor.biz/"){
body {margin:0 !important;}
#container, #banner, #blogcontainer { 
 background: none !important;
 width: inherit !important;
 }
.blogtitle, .description {padding: 0 !important;}

/* page contants horiz layout */
#wrapper {width: 78% !important;}
 #content {width: 71% !important;}
  #blog {width: 100% !important;}
 #left {width: 28% !important;}
  .side>table {width: 100% !important;}
#right {width: 21% !important;}
  #right table {width: 100% !important;}

/* hatena-widget, livedoor-blog-clap on right side */
.hatena-bookmark-widget,
.ldblogclap.itemOuter, .itemx
 {width: 100% !important;}

.article-body-inner p {
 display: inline !important;
 margin: inherit !important;
 line-height:1.2em;
 }
.blogbody>.main>table,
.blogbody>.main>table>tbody,
.blogbody>.main>table>tbody>tr,
.blogbody>.main>table>tbody>tr>td
 {display: block !important; width: inherit !important;}

#header2,
.blog-title-outer,
.article-body-inner a[href*="amazon"],
.article-body-inner>blockquote,
.column-inner-2>a,
.column-inner-2 br,
.article-tool-box-header,
#sidebar
 {display: none !important;}

/*== http://thyrving.livedoor.biz/archives/ --*/
#banner {height: auto !important;}
#banner h1.blogtitle {width: auto !important;}
#banner div.description {
 font-size: inherit !important; line-height: inherit !important;
 width: auto !important;
 }
#left, #right {background-color: white !important;}
.sidetitlebody, .side, .sidebottom,
.calendarheadbody, .calendarhead, .calbody, .calbottom
 {background-repeat: repeat !important;}
.titlebody {background-repeat: repeat-x !important;}
div.menu a{color: white !important;}
div#formbody table.form {width: 80% !important;}
}
