css:
/* @name: NAVER まとめ ja jp 2013/03 */
@-moz-document url-prefix("http://matome.naver.jp/odai/"){
/*== Naver free matome for all (http://matome.naver.jp/odai/) ==*/
/*- unwanted contents -*/
div[class^="MdPRBnr"],
div[class^="MdAd"]
 {display: none !important;}

/*- page wrap -*/
.LyWrap .lyWrapInner {
 width: inherit !important;
 min-width: 800px !important;
 }

/*- naver matome bar -*/
.LyHead .lyHeadInner {width: 100% !important; height: auto !important;}
.LyHead .lyHeadInner *[class*="Head"] {
 position: static !important;
 display: inline-block !important;
 top: auto !important; left: auto !important; right: auto !important;
 }

/*- topic top view -*/
.CTypeMTMListView .LyTop {margin: 0 !important;}
.MdMTMViewHead01 {min-width: 800px !important;}
.MdMTMViewHead01 .mdMTMViewHead01Inner { 
 height: auto !important;
 line-height: 1em !important;
 padding: 0 0.5em 0 !important;
 width: auto !important; 
 }

.LyTop .lyTopInner {width: 100% !important; padding-bottom: 0.5em !important;}
.MdHeading01 .mdHeading01Main {
 width: 100% !important; min-width: 580px;
 margin-right: -220px;
 }
.MdHeading01 .mdHeading01MainWithThumb .mdHeading01Txt
 {margin-right: 220px;}
.MdHeading01 .mdHeading01Sub {text-align: center !important;}

/*- topic bottom view -*/
.LyBtm .lyBtmInner,
.LyFoot .lyFootInner
 {width: 100% !important; min-width: 800px; padding: 0 !important;}
.MdCat01 ul {display: block; height: 25em !important;}
.MdCat01 ul li {width: 33% !important; min-width: 166px; height: 5em; margin-bottom: 0 !important;}
.MdCat01 li[class$="LiFirst"] {margin-top: -25em !important;}
.MdCat01 li[class*="Col02Li"] {margin-left: 33% !important;}
.MdCat01 li[class*="Col03Li"] {margin-left: 66% !important;}
.LyBtm .MdReporting01 {margin: 0 !important; text-align: right;}

/*- topic main view (2columns relayout to requid) -*/
.CTypeMTMList .LyContents .lyContentsWrap {background: none !important;}
.LyContents .lyContentsWrap {width: 100% !important;}
.LyMain {width: 100% !important; min-width: 500px; margin-right: -300px; padding-bottom: 0 !important;}
.LySub {padding-bottom: 0 !important;} 
.LyMain div[class^="ArMain"] {width: auto !important; margin-right: 300px !important;} 

/*- topc main view main column (left) -*/
.LyMain .MdMTMWidget01 {width: 100% !important;}
.LyMain .MdMTMWidget01 .mdMTMWidget01Content01 {width: 100% !important; margin: 0 !important; padding: 0 !important;}
.LyMain .MdMTMWidget01:hover {padding: 0 !important;}
.LyMain .ArMain04 {margin-top: 0 !important; margin-bottom: 40px;} 
.LyMain .MdMTMWidget01 .mdMTMWidget01ItemTwitterUserName01, 
.LyMain .MdMTMWidget01 .mdMTMWidget01ItemTweet01,
.LyMain .MdMTMWidget01 .mdMTMWidget01ItemTweetOption01
 {padding-left: 58px;}
.LyMain .ArMainSeparator01 {margin: 0.5em 0px !important;} 

/*- topic main view sub column (right) -*/
.ArSubWrap01 {position: static !important; top: inherit !important;}
}

@-moz-document url-prefix("http://matome.naver.jp/bp/"){
/*== naver official matome for bussiness (http://matome.naver.jp/bp/) ==*/
/*- page -*/
body {min-width: 800px !important;}
.LyWrap .lyWrapInner {width: 100% !important;}
.LyContents, .ArColWrap01 {
 background-image: none !important;
 background-color: white !important;
 width: auto !important; padding: 0 !important;
 }

/*- header -*/

/*- footer -*/
.LyFoot {
 padding: 0 !important;
 background: white !important;
 border-top: 2px solid gray;
 }

/*- main 3columns relayout -*/
.ArColWrap01>div[class^="ArCol0"] {
 width: 33% !important; min-width: 266px; margin: 0 !important;
 box-shadow:0 0  2px gray;
 }
div[class^="ArCol0"]>* {margin-left: 4px !important;}
div[class^="ArCol0"]>* * {
 display: inline-block !important;
 max-width: 100% !important;
 height: auto !important;
 position: static !important;
 float: left !important;
 top: inherit !important; left: inherit !important; right: inherit !important;
 }

/*- left column -*/
.MdBLDTtl01 .mdBLDTtl01Ttl,
.MdBLDTtl01 .mdBLDTtl01LinkTtl,
.MdBLDTtl01 .mdBLDTtl01LinkUl,
.MdBLDTtl01 .mdBLDTtl01Logo
 {width: 100%; margin-bottom: 0 !important; padding: 0 !important;}
.MdBLDTtl01 {border: none !important; padding: 0 !important;}

/*- center clumn -*/

/*- right column -*/
.MdBLDImg01 .mdBLDImg01Ul {width: auto !important;}
}
