css:
/*=============================================
  yahoo.co.jp login/out lliquid compact 2014/01
  ============================================*/
@-moz-document domain("login.yahoo.co.jp"){
/*--- unwanteds ---*/
[class^='pr'],
#yregtctx #yregtxt,
[id^='pr'],
#yregad,
.prAreaBottom,
dummy#dummy.dummy {display: none !important;}

/*--- globals ---*/
* {font-family: inherit !important;}


/*--- page containers ---*/
#yregwp, .yregwp {width: auto !important;}

/*--- master header ---*/
#msthd {margin: 0 !important;}
#msthd .logo {padding: 0 !important;}
#msthd .logo img{height: 1.5em !important; width: auto !important;}
#msthd .msthdlink {width: auto !important;}
#msthd .msthdlink ul {margin: 0 !important;}

/*--- login/logout search bar ---*/
#posS {margin: 0 !important; padding: 0 !important;}

/*--- login/logout footer ---*/
#yregft {padding: 0 !important;}
}

@-moz-document url-prefix("http://login.yahoo.co.jp/config/login?logout="){
/*--- after logout pane ---*/
.logoutMsg {margin: 0 !important; padding: 0 !important;}
.logoutMsg h1 {margin: 0 !important;}
#lnks #backToService
 {margin: 0 !important; padding: 0 !important; line-height: inherit !important;}
#lnks #anotherID {width: auto !important;}
#lnks .button {height: auto !important; padding: 0 !important;}

/*--- login/out method settings ---*/
#yredmods {width: 100% !important; margin: 0 !important;}
#yredmods .yrps {margin: 0 !important;}
#yredmods .yrps .cntnt {padding: 0 !important;}
#yredmods .yrps .cntnt .guideList li {
 width: 33.3% !important;
 margin: 0 !important; padding: 0 !important;
 font-size: inherit !important; text-align: center;
 }
#yredmods .yrps .cntnt .guideList li a {
 background: none !important;
 text-indent: 0 !important;
 width: auto !important; height: auto !important;
 }
}

@-moz-document url-prefix("https://login.yahoo.co.jp/config/login"){
/*--- login page url-prefix(https://login.yahoo.co.jp/config/login) ---*/
/*--- yahoo recomend  ---*/
#notices .info {margin: 0 !important; padding: 0 !important;}
#notices .info>div {box-sizing: border-box; width: 50% !important; padding: 0.5em;}
#notices .infoImg {display: block !important; float: left; padding-right: 0.5em}
#notices .info .title
 {display: block !important; width: auto !important; margin: 0 !important;}

/*--- login form ---*/
#yreglg {
 position: static !important;
 top: 0 !important; left: 0 !important;
 width: auto !important;
 margin: 0 !important;
 }
.crnt, .crnb {width: 100% !important; background: none !important;}
.crnt .crnl, .crnb .crnl {right: 0 !important; background: none !important;}
#yregct {margin: 0 !important;}
#yreglg {right: inhrit !important;}
.yregbx {margin: 0 !important;}
.yregbxi {padding: 0 !important;}
#yregloginfield {width: auto !important;}
#yregloginfield label {display: none !important;}
#yregloginfield * {
 display: inline !important; position: static !important; float: none !important;
 margin: 0 !important; padding: 0 !important;
 }
#yregloginfield .placeholder {height: auto !important;}
#yregloginfield #username {height: auto !important; padding: 0 1important;}
#yregloginfield .cols {margin: 0 !important;}
#yregloginfield .yreg_ipt, .btnLogin {height: auto !important;}
#login_form .persistency {margin: 0 !important; text-align: right;}

/*--- account manage ---*/
.top .yregbxi .links li {list-style: disc; float: left; margin: 0 0 0 2em !important;}
}
