css:
body {background: url('http://static.tumblr.com/joj0uwh/DrVmio58h/chimchim.png') top left #705D4F no-repeat fixed !important;}
*{text-shadow:none !important; color:#B4A192 !important;} 
 
#content 
	{background-color:#4A3F37 !important; background: rgba(74,63,55,.8) !important;} 
#nav .nav_item.active 
	{background-color:#4A3F37} 
.permalink  
	{background: none !important; 
	width: 16px !important; 
	height: 16px !important; 
  	background: #4A3F37) !important; 
  	border-bottom-left-radius: 10px !important; 
  	border-top-right-radius: 10px !important; 
  	margin-top: 3px !important; 
  	margin-right: 4px !important;} 
h1,h2,h3,h4,h5,h6 
	{color:#fff !important;} 
a, a:visited 
	{color:#CDAA7D !important;} 
a:hover 
	{color:#fff !important;} 
 
 
 

 
 
 
#dashboard_switch_blog_menu_current, 
#dashboard_switch_blog_arrow_and_divider, 
#dashboard_switch_blog_arrow_current 
	{background-image:none !important;} 
#new_post_notice_container 
	{color:#fff !important;} 
#new_post_notice_container a 
	{color:#fff !important;} 
ol#posts li.notification 
	{background-color: #4A3F37 !important; 
         border-bottom: 2px solid #B4A192 !important; 
         color: #B4A192 !important; opacity: .80;} 
 
 
 
.notification_type_icon{display:none !important;} 
.selected .following, 
.selected .followers, 
.selected .settings, 
.selected .posts, 
.selected .messages, 
.selected .drafts, 
.selected .queue,  
.selected .likes  
	{color: #A099A1 !important;  
	background: #726054 !important;} 
 
 
 
a.explore_more_tags, 
a.add_and_remove, 
a.customize, 
a.mass_editor 
        {background-color:#726054 !important;   
	background-image:none !important; 
	border:0px !important;} 
div.gradient, 
div.dashboard_nav_item,  
a.permalink 
	{background-color:#726054 !important; 
	opacity:.9;  
	background-image:none !important; 
	border:0px !important;} 
.content.is_media,  
.mask.top,  
.mask.bottom  
	{background: none !important; } 
.post.photo_post .photo,  
.post.photo_post .video_thumbnail  
	{border-radius: 16px !important;} 
.post.photo_post .photo,  
.post.photo_post .video_thumbnail  
	{-moz-box-shadow: 1px 1px 3px #000000 !important;} 
 
 
 
#auto_pagination_loader 
	{background: #FFFFFF !important;} 
div.return_to_top_icon{	background: none !important; 
	width: 0px !important; 
	height: 0px !important; 
	margin-top: 0px !important; 
	border-bottom: solid 30px white !important; 
	border-right: 15px solid transparent !important; 
	border-left: 15px solid transparent !important;} 
 
 
 
 
#left_column #posts .post .permalink 
	{background-color:#4A3F37;} 
#left_column #posts .notification  
	{background-color:#4A3F37 !important; 
        color:#666 !important; 
        border-bottom-color: #eee !important;} 
#left_column #posts .post .post_avatar 
	{-moz-border-radius: 38px !important;  
	-webkit-border-radius: 60px !important;  
	width: 64px !important; height: 64px !important;  
	margin: 0px !important;} 
#left_column #posts .post .sub_avatar_inner 
	{-moz-border-radius: 38px !important;  
	-webkit-border-radius: 60px !important;  
	width: 32px !important; height: 32px !important;  
	margin: 0px !important;} 
 
 
 
 
.new_post_label_icon{background-image:none !important;}
#new_post{background: url('http://static.tumblr.com/joj0uwh/ZGsmio6uc/dash.png') center no-repeat; background-color:none !important;}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:0px;
    opacity: 0;
    display: block;}
.new_post_label{
    font-size:0px !important;
    padding-top:0px !important;
    opacity: 0;}
 
 
 
 
#logo {height: 0 !important;
        width: 0 !important;
	padding-left: 240px !important;
	padding-top: 70px !important;
        margin-top: 6px !important; 
	background: url(http://static.tumblr.com/joj0uwh/l40mio8lc/imelt.png) no-repeat !important;}



#return_to_top .return_to_top_icon{

background: url('http://static.tumblr.com/joj0uwh/3RHmio8b2/hnmrs.png') no-repeat transparent!important}

.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){background-image:url("http://static.tumblr.com/joj0uwh/3RHmio8b2/hnmrs.png")!important;background-repeat:no-repeat!important;width:23px!important;height:77px!important;margin-top:-12px!important}.selection_nipple.white{background-position:0 0!important;width:35px!important;height:35px!important}.selection_nipple:not([class="selection_nipple white"]){border-bottom:solid 11px rgba(0,0,0,0)!important}#inbox_button.tab.iconic.selected .selection_nipple:not([class="selection_nipple white"]){margin-left:-10px!important}
