css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iichan.hk") {
/* Name: MSPA-iichan (#crap #bluh #fuckasses)*/
html, body{
background-color:#5a5a5a !important;
font-family:serif;
}
.adminbar a{
color:#c6c6c6 !important;
}
.adminbar{
color:#c6c6c6 !important;
}
body a{
color:#800000 !important;
}
.commentpostername{
color:#000000 !important;
font-size:1em !important;
}
.hr, .theader, .banner,.sublogo {
display:none !important;
}
div>blockquote{
color:#c6c6c6 !important;
}
div>blockquote a, div>blockquote .unkfunc{
color:white!important;
}
.doubledash{
color:white !important;
}
.filetitle{
color:#000000 !important;
font-size:1.2em !important;
}
.filesize{
color:#000000 !important;
margin:1px !important;
}
.filesize a{
color:#000000 !important;
}
.footer a{
color:#c6c6c6 !important;
}

.highlight{
background-color:#c6c6c6 !important;
border: 2px dashed #678CB1 !important;
color:#000000 !important;
}

.highlight a{ 
color:blue !important;
}
label{
color:#000000 !important;
font-size:1em !important;
}
blockquote ol li{
margin:auto !important;
}
.logo{
background: url(http://www.mspaintadventures.com/images/bannerframe.png) center no-repeat !important;
color: #c6c6c6 !important;
font-size:2.5em !important;
margin:3px;
}
.omittedposts{
color:#c6c6c6 !important;
}

.pcount{
	color:#FF0000 !important;
}
.postblock {
background-color:#c6c6c6 !important;
color:#800000 !important;
font-weight:800 !important;
}
.postername{
color:#000000 !important;
font-size:1em !important;
}
textarea, input{
background-color:#FFFFFF !important;
}
.reply{
background-color:#c6c6c6 !important;
margin:1px !important;
padding:0 !important;
border: 2px solid #5a5a5a !important;
color:#000000 !important;
}
.reply a{
color:blue !important;
}
.replytitle{
color:#440A7F !important;
font-size:1em !important;
}
.reflink a{
color:blue !important;
font-size:1em !important;
}
span.spoiler, span[onmouseout*="color=\'black\'"] {
  background:none repeat scroll 0 0 #575757 !important;
  color:#575757 !important;
}

span.spoiler:hover, span[onmouseover*="color='white'"]:hover {
	color: #48B0FD !important;
}
td{
color:#c6c6c6 !important;
}
td a{
color:#c6c6c6 !important;
}
.thumb {  
  float:left !important;
  padding:3px !important;
}
.unkfunc{
	color:#28731E !important;
}
/*frame*/
.category{
background-color:#5a5a5a !important;
}
.header{
background-color:#EEEEEE !important;
color:#440A7F !important;
}
/*captcha*/
td > img{
background-color:#c6c6c6 !important;
}
/*dollfixes*/
.de-refmap{
color:#000000 !important;
}
.de-pview blockquote p{
color:#000000 !important;
}
}
