/*****************************************/
/****** Linux Journal (almost) KISS ******/
/********** Created by: donbex ***********/
/****** Based on styles by tjwoosta ******/
/********* and on the late style *********/
/* Linux Journal - Minimal, Dark / Black */
/************* by vb64h3j36h *************/
/*****************************************/

/******************************************
 
To change the colors of this style use your
text editors global search and replace
feature to replace the following colors.

#262626 (background color)
#3e3e3e (border color)
#8a8a8a (text color)
#a7a15e (link color)
#a67b07 (visited link color)
#556b2f (header/link hover color)
#af5f00 (code/emphasis color)
#705b46 (list/italics color)

******************************************/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("linuxjournal.com") 

{
/*>>>Remove Content<<<*/

#header,
#header-top,
#footer,
#lead-gen-wrapper,
#sidebar-right,
.feed-icons,
ul.links.inline,
#comments,
.user-signature,
#facebook-like,
.g-plusone-wrapper,
#searchbox,
.read-more
{
 display: none !important;
}


/*>>>Borders<<<*/

#block-views-slideshow-block_1 .views-row-even,
#block-views-slideshow-block_1 .views-row-odd,
img
{
 border: none !important;
}

thead th,
.view-community-events .views-row-even,
.view-community-events .views-row-odd,
#currentissue
{
 border-color: #3e3e3e !important;
}

.issue
{
 border: 2px solid #3e3e3e !important;
 border-radius: 8px !important;
}

.item-list ul.pager li .active
{
 border: 0px solid #3e3e3e !important;
 border-radius: 8px !important;
}

a
{
 outline: none !important;
}


/*>>>Backgrounds<<<*/

body,
#main,
#navbar,
#block-views-slideshow-block_1,
.view-videos.view-id-videos.view-display-id-attachment_1.view-dom-id-1,
.view-id-videos.view-display-id-page_1.view-dom-id-2 .views-field-title,
.page-participate #content h2,
#currentissue,
.issue,
.item-list ul.pager li .active,
.resizable-textarea .grippie
{
 background-color: #262626 !important;
}

#primary ul.links li.menu-71 a,
#primary ul.links li.menu-1803 a,
#primary ul.links li.menu-187 a,
#primary ul.links li.menu-1802 a,
#primary ul.links li.menu-150 a,
#primary ul.links li.first a,
#primary ul.links li.menu-1800 a
{
 background: none !important;
}


/*>>>Text<<<*/

.node-teaser div.content p,
.node .submitted,
body
{
 color: #8a8a8a !important;
}

li a.active,
a
{
 color: #a7a15e !important
}

a:hover,
h2,
h1
{
 color: #556b2f !important;
}

/*
.page-participate #content h2
{
 text-decoration: underline !important;
}
*/

.item-list ul.pager li .active:hover
{
 color: #556b2f !important;
}

pre,
code,
tt
{
 color: #af5f00 !important;
}

a:hover
{
 text-decoration: none !important;
}


/*Positions*/

#page
{
 position: relative;
 top: -20px !important;
}

#content
{
 position: relative;
 left: 95px !important;
}

.views-view-grid
{
 position: relative;
 right: 194px !important;
}

.views-field-picture, .views-field-name
{
 position: relative;
 left: 194px !important;
}

#navbar
{
 padding: 12px !important;
 width: 600px !important;
 height: 14px !important;
 position: relative;
 right: 535px !important;
 top: -10px !important;
}

#primary ul.links
{
 width: 600px !important;
}


/*>>>Various<<<*/

.sidebar-right #content
{
 margin-right: 0 !important;
}

#content-area
{
 padding-bottom: 0 !important;
}

#page
{
 max-width: 800px !important;
}

.node-teaser .imagecache-teaser_linked img
{
 margin-bottom: 20px !important;
}


/*>>>BG2<<<*/

#primary ul.links
{
 background: url("data:image/jpeg;base64,%3D") !important;
}

}
/*****************************************/


@-moz-document url-prefix("http://www.linuxjournal.com/video") 
{

#navbar
{
 position: relative;
 right: 555px !important;
 top: -10px !important;
}

}
