css:
@-moz-document domain("wikipedia.org") {
div#siteSub {display:none!important;}



body {font-family: Helvetica Neue !important;font-size: 1.4em !important;} 



.firstHeading {
border: none !important;
margin-left: -6px !important;
margin-bottom: 0px !important;
margin-top: -3px !important;
font-size: 2.75em !important;
font-family: AquilineTwo !important;

padding: 3px 0 38px 10px !important;
}

div#contentSub {display:none !important;}




#column-one, #footer
  { display: none !important; } 


div#content.mw-body
{ 
width: 960px !important;
margin: 15px auto 25px auto !important;  
border: 2px solid rgb(0,0,0) !important;
border-radius: 8px !important;

padding: 12px 24px 16px 24px !important;
background: url(http://i.imgur.com/0DzumS3.png) center center fixed !important;}

body {background: url(http://i.imgur.com/OpQippF.png) center center fixed !important;} 



#mw-panel {display:none!important;}
.noprint {display:none!important;}

#panel, #head,#page-base
  { display: none !important; } 
  
  
  


h1, h2, h3, h4, h5, h6 {
border-bottom: none !important;
font-family: Helvetica !important;
margin-bottom: 0px !important;
}

h2 .mw-headline {
font-size: 1.1em !important;
font-weight: 500 !important;
}
h2 {
border-bottom: 1px solid #CCC !important;
}







  
  
  
  
  
  
  
  
  
  


.infobox {
    border:none!important;
    background-color: #F5F5F5 !important;
    box-shadow: 0px 0px 3px 0 #555 !important;
    border-radius: 3px !important;
    }

.infobox:hover {
    background-color: #F1F1F1 !important; 
    box-shadow: 0 0 3px #454545 !important;
    -moz-transition: 400ms
    }


.toccolours, .thumb {
    border: 1px solid rgba(0,0,0,0) !important;
    box-shadow: 0px 0px 2px 0 #555 !important;
    border-radius: 3px !important;
    }
.thumb:hover {
    background-color: #F1F1F1 !important; 
    box-shadow: 0 0 3px #454545 !important;
    -moz-transition: 400ms
    }

div.thumb.tright {margin:16px 24px!important;}

.thumbinner {
    padding: 4px !important;
    border: none !important;
    box-shadow: 0px 0px 2px 0 #CCC !important;
    border-radius: 3px !important;
    background-color: #F5F5F5 !important;
    }

.toc {
    border:none!important;
    box-shadow: 0px 0px 2px 0 #777 !important;
    margin: 8px !important;
    border-radius: 3px !important;
    }
.toc h2 {display:none !important;}
.toc:hover {
    background-color: #F5F5F5 !important; 
    box-shadow: 0 0 4px #666 !important;
    -moz-transition: 400ms;
    }
div.toctitle {text-decoration: none !important;}
span.toctoggle {display:none !important;}


span.editsection {display: none !important;}

a:link, a:active { 
color: #365696 !important; 

}



a:hover {
color: #409fff !important; 
} 



.mw-search-result-data, .mw-specialpage-summary, .mw-search-formheader, .mw-search-createlink {display:none !important;}
.mw-search-results {padding-top: 15px !important;}
div.mw-jump {display:none!important;}
table#mw-search-top-table tr td input {display:none!important;}


sup[class="reference"], span[id="References"], ol[class="references"] {display:none !important;}


div#mw-articlefeedback {display:none!important;}


#coordinates {display:none !important;}



.collapseButton {display:none !important;}


div#protected-icon {display:none !important;}


table[class="metadata plainlinks ambox ambox-move"] {display:none !important;}
}
