css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("soymallorquinista.mforos.com") {
tbody > tr > td div > a > img {display:none !important;}
td > img {display:none !important;}
br {line-height:0 !important;}
object {display:none !important;}
body { background-image:url(http://farm2.static.flickr.com/1439/1020418737_f2e49faa53_o.jpg) !important;}
table, table tbody, table tr td{background-color:transparent !important;}
#tabla_titulo_obj {font-family:Century Gothic !important;background-color:transparent !important;color:#500000 !important;}
#tabla_titulo_obj a {color:#500000 !important;font-size:14px !important;}
.tabla_categorias {background-color:transparent !important;-moz-border-radius:5px;font-family:Century Gothic !important;-moz-opacity:0.65 !important;}
tr.tabla_categorias td {background-color:transparent !important;color:black !important}
.tabla_texto_1, .tabla_sombra_1{ font-family:'helvetica neue',arial,helvetica,sans-serif;}
#Tabla1_obj a:link,#Tabla1_obj a:visited,#Tabla2_obj a:link,#Tabla2_obj a:visited  {color:#ffbbbb !important;font-family:Century Gothic !important;}
#tabla_categorias_obj a {color:#ffffff !important;font-family:Century Gothic !important;font-size:18px !important}
#tabla_categorias_obj {color:#900000 !important;font-family:Century Gothic !important;}
noscript img {display:block !important;color:red !important;}
.contenedor_foro {max-width:3000px !important;}
#ma_toolbar, #ma_toolbar #ma_auxie6_bg, #ma_toolbar .matb_search_form * {background-color:transparent !important;}
#ma_toolbar, #ma_toolbar a.matb_boton:hover, #ma_toolbar a.matb_menuSel, #ma_toolbar div.matb_menu *, #ma_toolbar div.matb_menu div.sepSubMenu {border:0 !important;}
.tabla_texto_1 + .tabla_texto_1 , .tabla_texto_2 + .tabla_texto_2 {background-color:transparent !important;display:none !important;}
.tabla_texto_1 , .tabla_texto_2 {background-color:transparent !important;-moz-opacity:0.88 !important;}
.tabla_texto_1 td ,.tabla_texto_2 td {-moz-border-radius-bottomright:30px !important;background-color:#500000 !important;-moz-opacity:0.98 !important;}
.tabla_texto_1 td span ,.tabla_texto_2 td span {font-size:8px !important;}
.tabla_texto_1 td span a,.tabla_texto_2 td span a {font-size:12px !important;font-weight:bold !important;}
}
.tabla_texto_1 td:first-child,.tabla_texto_2 td:first-child {padding-left:30px  !important;padding-top:30px !important;color:transparent !important;background-color:#990000 !important;-moz-border-radius-topleft:30px !important;}
.tabla_texto_1 td:first-child img,.tabla_texto_2 td:first-child img {display:none !important;}
