css:
@namespace url(http://www.w3.org/1999/xhtml);

@charset "utf-8";
@-moz-document domain('news.ycombinator.com') {


/* HN Red theme */

body { margin: 0; }

/* change greens to red */
font[color="#3c963c"] { color: #A00 !important; }

a:link    { color:#111 !important; }
a:visited { color:#828282 !important; }

/* maintain default styles */
.default { color:#828282 !important; }
.admin   { color:#000000 !important; }
.title   { color:#828282 !important; }
.adtitle { color:#828282 !important; }
.subtext { color:#828282 !important; }
.yclinks { color:#828282 !important; }
.comhead { color:#828282 !important; }
.dead    { color:#dddddd !important; }
.subtext a:link, .subtext a:visited { color:#828282 !important; }

.dead a:link, .dead a:visited { color:#dddddd !important; }
.topsel a:link, .topsel a:visited { color:#ffffff !important; }
.comhead a:link, .subtext a:visited { color:#828282 !important; }

/* hover to see dead */
.default:hover .dead { color:#828282 !important; }
.default:hover .dead a:link, .default:hover .dead a:visited { color:#333 !important; }

input[type=submit] { cursor: pointer !important; }
textarea, input {
    border: 1px solid #888 !important;
    background-color: #F6F6F6 !important;
    box-sizing: content-box;
    display: block !important;
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    border-radius: 2px;
}


/* hide the orange logo */
body > center > table > tbody > tr > td[bgcolor="#ff6600"] > table > tbody > tr > td:first-child img { display: none !important; }

/* replace with red logo */
body > center > table > tbody > tr > td[bgcolor="#ff6600"] > table > tbody > tr > td:first-child a {
    padding: 1px !important;
    border: 1px solid #333;
    height: 18px !important;
    width: 18px !important;
    display: block;
    transform: rotate(-45deg);
    background: no-repeat 1px 1px url(data:image/png;base64,);
}

/* "Hacker News" and other title text */
.pagetop b {
    font-size: 16px;
    color: #333 !important;
    width: 100px !important;
    margin-left: 8px;
}
/* top nav links always red */
.pagetop a { color: #A00 !important; }

/* expand color areas all the way, this removes side gutters */
body > center > table { width: 100% !important; }

/* make gutters come from inner areas instead */
body > center > table > tbody > tr > td > table, /* most site areas */
form[action^="/r"] > table, /*submit form */
form[action^="/x"] {
    width: 100% !important;
    padding: 2px 7% !important;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
}

/* make header thicker */
body > center > table > tbody > tr > td[bgcolor^="#ff6600"] > table {
    padding-top: 20px !important;
    padding-bottom: 20px !important;
}

/* regular areas */
table[bgcolor] { background-color: #fAfAfA !important; }

/* orange areas -> grey */
td[bgcolor="#ff6600"] { background-color: #CCC !important; }

/* yellow areas -> light red */
td[bgcolor="#ffffaa"] {
    background-color: #EEDDDD !important;
    color: #666 !important;
}
td[bgcolor="#ffffaa"] td { color: #6A6A6A !important; }


/* center area, list view */
/* post numbers */
body > center > table > tbody > tr[style^=height]:not([bgcolor]) + tr td.title[align][valign] {
    width: 10px !important;
    text-align: left !important;
    padding-left: 5px !important;
}

/* add padding where the spacers used to be  */
body > center > table > tbody > tr[style^=height]:not([bgcolor]) + tr > td > table[cellspacing][cellpadding] > tbody > tr[style="height:5px"] {
    height: 12px !important;
}

/* hide nasty spacer that shows on some pages, such as /jobs */
body > center > table > tbody > tr[style^=height]:not([bgcolor]) + tr > td > img:first-child {
    display: none;
}

/* post titles */
body > center > table > tbody > tr[style^=height]:not([bgcolor]) + tr td.title:not([align]) {
    font-size: 16px !important;
}

/* footer; this is the orange "hr" table */
body > center > table > tbody > tr:last-child > td > table[width] {
    padding: 0 !important;
}

/* don't show the up/down gifs, use characters */
a[id^=up_] img, a[id^=down_] img { display: none; }
a[id^=up_]:before { content: "▲"; color: #828282; }
a[id^=down_]:before { content: "▼"; color: #828282; }

form[action="http://www.hnsearch.com/search#request/all"] { width: 220px; }

form[action="http://www.hnsearch.com/search#request/all"] input { display: inline; float: right; }
}
