css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lineup.bonnaroo.com") {

.w {
    width:32% !important;
    float: left !important;
    text-align: left !important;
    list-style: none !important;
    }
    
.band { 
    width:100% !important
    /*-moz-column-width: 300px !important;  
    -moz-column-rule: 4px solid maroon;
    -webkit-column-width: 300px !important;  
    -webkit-column-rule: 4px solid maroon;*/
    }

li a {
    text-transform: none !important; 
    white-space: normal !important;
    font-size: 20px !important;
    font-weight: 200 !important;
    font-style: normal !important
    }

.ds-lineup li:nth-child(odd) a {
    color: #ffffff !important;
    }

.ds-lineup li:nth-child(odd) a:hover {
    color: #ffffff!important;
    }

.ds-lineup li:nth-child(even) a {
    color: #AAAAAA !important;
    }

.ds-lineup li:nth-child(even) a:hover {
    color: #AAAAAA !important;
    }


.ds-bullet {
    display: none !important;
    
    }
    
.ds-tooltip {
    width: 300px !important;
        
    }
}
