css:
@-moz-document url-prefix("http://poneyvallee.com/forum/") {
body {
	background : #FFFFFF url("http://img11.hostingpics.net/pics/836213konachancom45384hatsunemikukagaminelenkagaminerinkaitokamuigakupomegurinelukameikovocaloidyahako.jpg") center fixed !important;
	Background-size: 100% !important;
	color : #444444 !important;
}
.left, .right, .center {
	background: none !important;
}

#brdheader {
	background: rgba(136,77,167,0.4) url("http://img11.hostingpics.net/pics/115085474282test5.gif") no-repeat right top !important;
	background-size : auto 150px !important;
	border : 2px black solid !important;
	margin-top:5px !important;
}


#brdmenu[style="background-color:#315B45;margin-left:5px;margin-right:5px"] {
	background: transparent !important;
}
#brdmenu a:link { color: white !important;}
#brdmenu a:visited { color: white !important;}
#brdmenu a:hover { color: pink !important;}
#brdmenu a:active { color: pink !important;}

a:link { color: pink !important;}
a:visited { color: pink !important;}
a:hover { border-bottom : black 2px solid !important;}

#brdheader .box{
	background: transparent !important;
	border : none !important;
}
#brdmenu {
	background: transparent !important;
}
#brdmenu a{
	color : black !important;
	border-bottom: none !important;
}
#brdmenu a:hover{
	color : black !important;
	border-bottom: black dotted 2px !important;
}
#brdwelcome li {
	color : black !important;
}
#brdwelcome li a{
	color : black !important;
	border-bottom : 1px dotted black !important;
}



h2 {
	background: -moz-linear-gradient(left, black, black, orchid, darkcyan, yellow) repeat fixed !important;
	color : darkcyan !important ;
	border : 2px transparent solid !important;
	border-bottom : none !important ;
}

thead th, .arrondis_bas {
	background: white !important;

}
.arrondis_bas {
	border : 2px yellow solid !important;
}

tbody td, thead th {
	background: transparent !important;
	border : none !important ;
	border-top : 1px solid black !important ;
}
tbody h3 a {
	border-bottom : none !important ;
}


.tclcon {
	color : white !important ;
}
.tclcon a{
	color : pink !important ;
}
.tclcon a:hover{
	border-bottom : black 2px solid !important ;
}
.tclcon p a{
	border-bottom : orchid 1px solid !important ;
}

.tc2, .tc3 {
	color : white !important ;
}

.tcr {
	color : pink !important ;
}
.tcr a {
	color : white !important ;
	border-bottom : orchid 1px solid !important ;
}
.tcr a:hover {
	color : white !important ;
	border-bottom : black 2px solid !important ;
}

.conl, dd {
	background: transparent !important ;
}
#brdstats .box, #brdfooter .box {
	background: rgba(136,77,167,0.4) url(http://img11.hostingpics.net/pics/372796test6.gif) no-repeat center  !important;
	border : 2px black solid !important;
	color : black !important ;
}
#brdstats .box a, #brdfooter .box a{
	color : white !important ;
	border-bottom : none !important;
}

.icon.inew {
	background:transparent url("http://img11.hostingpics.net/pics/708769test7.jpg") left no-repeat !important;
	height : 50px !important; 
	margin-right : 20px  !important; 
}
.icon {
	background:transparent url("http://img11.hostingpics.net/pics/508476test8.png") left no-repeat !important;
	height : 50px !important; 
	margin-right : 20px  !important; 
}


.blocktable .box {
	background: rgba(136,77,167,0.4) !important;
	border : 2px black solid !important;
}

.isticky .tclcon a {
	border-bottom : 1px solid white !important;
}

DIV.blockpost DIV.box, DIV.postright, DIV.postfootright {background : transparent !important;}
DIV.postright, DIV.postfootright {BORDER-LEFT-COLOR: transparent !important;}
DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {background : transparent !important;}

.postmsg a {
	color : yellow !important;
	border-bottom: 1px dotted yellow !important;
}
.postmsg a:hover {
	color : yellow !important;
	border-bottom: 1px dotted black !important;
}
.usertitle {
	color : white !important;
}
.postleft dt a{
	color : pink !important;
	border-bottom: none !important;
}
.usercontacts a{
	color : yellow !important;
	border-bottom: 1px dashed yellow !important;
}
.linkst a{
	color : white !important;
	border-bottom: 1px solid pink !important;
}

DIV.blockpost H2 SPAN.conr {COLOR: white !important;}

DIV.postright {
	BORDER-LEFT: solid 198px transparent !important;
}
.box, .blockpost {
	background : rgba(136,77,167,0.4) !important;
}
.blockpost .box {
	border : 2px black solid !important;
}
.blockpost a {
	color : pink !important ;
	border-bottom : 2px solid white !important ;
}

.pun {
  color: rgb(255,128,0) !important;
color : white !important;
}

}
