css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www2.mufgcard.com") {
body:not(.narrow) {
width:960px;
margin:0 auto;
}

body.wide{
width:1021px;
margin:0 auto;
}

body.narrow{
width:560px;
  margin:0 auto;
}

body:not(.narrow) #header{
width:960px;
height:59px;
overflow: hidden;
display:block;
background:url(https://www2.mufgcard.com/inet/image/ws_header_wide_a.gif) no-repeat 100% ;
}

body.wide #header{
width:1021px;
}

body.narrow #header{
height:50px;
}

#header img{
  clip:rect(0px,688px,751px,0px); position:absolute;
}

#contents #navi ul{
list-style:none;
}

#contents #main{
	width:720px;
}

h1{

}

h2{
width:100%;
background: transparent url(https://www2.mufgcard.com/inet/image/ws_h2_bg.gif) repeat-y 0 0 scroll;
background-image: url(https://www2.mufgcard.com/inet/image/ws_h2_bg.gif),
    url(https://www2.mufgcard.com/inet/image/ws_h2_bg.gif);
background-repeat: repeat-y;
background-position:0 0,100% 0;
}

div.loginMsg{
width:620px;
}

div.logout img{
width:96px;
  height:30px;
}

#supportNavi{
width:770px;
}

.index #contents #main{
width:740px;
}

.index #personalInfo{
width:740px;
 background-repeat:repeat-x !important;
}

.wide div.loginMsg{
width:700px;
}

.index h2,
.index #mainColumn,
dl.recommend{
  width:550px;
}

.index #mainColumn h2{
background-position:100% 100% !important;
}

dl.recommend dd{
width:460px;
}
.index div.useful table.pointInfo{
width:740px;
}

input[id="userID"],
input[id="password"]{
	width:18em;
}
}
