@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/******************  Common *********************/

/* nomal */
#urlbar-reload-button,
#navigator-toolbox:not([mode="text"]) #reload-button image,
#navigator-toolbox:not([mode="text"]) #ctr_reload-button image,
#navigator-toolbox:not([mode="text"]) #stop-button image,
#navigator-toolbox:not([mode="text"]) #ctr_stop-button image
{ 
    list-style-image: url("data:image/png;base64,") !important;
    -moz-image-region: auto !important; 
    background:none!important;
    border:0px!important;
    box-shadow:none!important;

    opacity: /*[[mytransparency]]*/;
}

/* Loading & MouseOver */
/*======  View only icon =======*/
#urlbar-stop-button,
#urlbar-reload-button:hover,
#navigator-toolbox:not([mode="text"]) #stop-button:not([disabled="true"]) image,
#navigator-toolbox:not([mode="text"]) #ctr_stop-button:not([disabled="true"]) image,
#navigator-toolbox:not([mode="text"]) #reload-button:hover image,
#navigator-toolbox:not([mode="text"]) #ctr_reload-button:hover image
{
    list-style-image: url("data:image/png;base64,") !important;
    -moz-image-region: auto !important; 
    opacity: /*[[mytransparency]]*/;
}

/*  border */
#navigator-toolbox:not([mode="text"]) #reload-button:hover,
#navigator-toolbox:not([mode="text"]) #ctr_reload-button:hover,
#navigator-toolbox:not([mode="text"]) #reload-button:active,
#navigator-toolbox:not([mode="text"]) #ctr_reload-button:active,
#navigator-toolbox:not([mode="text"]) #stop-button:not([disabled="true"]):hover,
#navigator-toolbox:not([mode="text"]) #ctr_stop-button:not([disabled="true"]):hover{
    background-image: linear-gradient(rgba(255, 255, 255, 0.6), rgba(255, 255, 255, 0.1))!important;
    border:1px solid  !important;
    border-color: rgba(23,51,78,0.2) !important;
    border-radius:2px !important;
}
#navigator-toolbox:not([mode="text"]) #reload-button:active,
#navigator-toolbox:not([mode="text"]) #ctr_reload-button:active{
    border-color: rgba(23,51,78,0.4) !important;
}

/***********************  View icon fix ***********************/
/* nomal */
#urlbar-reload-button,
#navigator-toolbox[mode="icons"] #reload-button image,
#navigator-toolbox[mode="icons"] #ctr_reload-button image,
#navigator-toolbox[mode="icons"] #stop-button[disabled] image,
#navigator-toolbox[mode="icons"] #ctr_stop-button[disabled] image{ 
    margin:-38px -10px -30px -10px!important;
    height:50px;
    width:auto;
}
/* Loading & MouseOver */
#urlbar-stop-button,
#urlbar-reload-button:hover,
#navigator-toolbox[mode="icons"] #stop-button image,
#navigator-toolbox[mode="icons"] #ctr_stop-button image,
#navigator-toolbox[mode="icons"] #reload-button:hover image,
#navigator-toolbox[mode="icons"] #ctr_reload-button:hover image{
    margin:-38px -10px -30px -10px!important;

}

/*******************  View icon & text fix ********************/
/* Reload Button & Stop Button*/
#navigator-toolbox[mode="full"] #reload-button image,
#navigator-toolbox[mode="full"] #ctr_reload-button image,
#navigator-toolbox[mode="full"] #stop-button image,
#navigator-toolbox[mode="full"] #ctr_stop-button image{ 
    margin:-30px 0px -15px 0px!important;
    height:60px;
    width:auto;
}

/********************* urlbar fix *******************/
/* nomal */
#urlbar-reload-button image,
#urlbar-stop-button image{
    height:40px!important;
    width:auto!important;
}
/* Loading & MouseOver */
#urlbar-reload-button,
#urlbar-stop-button,
#urlbar-reload-button:hover{
    margin:-10px -10px -10px 0px!important;
    height:20px!important;
    width:auto!important;
}
