@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/* 読み込み中アイコンの位置・サイズ指定  (throbber position & size) */
#navigator-throbber>image ,
#status4evar-throbber-widget image{
    margin-top:    -15px;
    margin-right:    -5px;
    margin-bottom: -10px;
    margin-left:   -5px;
   
    height: 50px;
    width:  auto;
}

/* throbberアイコン（読み込み中じゃないとき）(non-active throbber) */
#navigator-throbber,
#status4evar-throbber-widget image{
    list-style-image: url("data:image/png;base64,") !important;
    opacity: /*[[mytransparency]]*/ !important; 
    -moz-user-focus:normal !important;


}

/*----------------------------------------------------*/


/* 読み込み中アイコン (busy throbber ) */
#navigator-throbber[busy="true"],
#status4evar-throbber-widget[busy="true"] image,
#navigator-throbber:hover,
#status4evar-throbber-widget image:hover{
    transform: rotate( 0 )!important;
    list-style-image: url("data:image/png;base64,") !important;
    opacity: /*[[mytransparency]]*/ !important; 
}
/*----------------------------------------------------*/
