css:
@-moz-document url-prefix("http://nodejs.org/") {

/* remove backgrounds and borders */
body, pre { background: transparent !important }
* { border: none !important }

/* 3 columns */
body { -moz-columns: 3; -moz-column-gap: 30px }

/* full width content without sidebar */
#content { width: auto !important; margin: 0 !important }
#column2 { display: none }
#column1 { width: auto !important; float: none !important }

/* hide unwanted stuff */
#intro, #column1 > header, #gtoc, #toc, #footer, a.mark { display: none }

/* adjust spacing */
body { padding: 0 !important }
header h1 { margin: 0 !important }
p { margin: 0 0 2px 0 !important }
ul { margin: 0 20px 2px !important }
li { margin: 0 0 !important }
pre { margin: 0 !important; padding: 4px 10px !important }

/* adjust font sizes */
body, #column1, .apidoc #apicontent p, .apidoc #apicontent li { font-size: 8px !important; line-height: 9px !important }
tt, code { font-size: 7px !important; line-height: 9px !important }
pre, pre * { font-size: 6px !important; line-height: 7px !important }
h1 { font-size: 11px !important; line-height: 13px !important; margin: 1em 0 2px !important }
h1:first-child { margin-top: 0 !important }
h1 + pre { padding: 0 0 6px !important }
h2 { font-size: 10px !important; line-height: 12px !important; margin: 1em 0 2px !important }
h2 + h2 { margin: 0 0 0.5em }
h3 { font-size: 9px !important; line-height: 10px !important; margin: 1em 0 2px !important }
h3 + h3 { margin: 0 0 0.5em }
h4, h5, h6 { font-size: 8px !important; line-height: 10px !important; margin: 1em 0 0.66em !important }

}
