css:
@-moz-document url-prefix("http://www.jeuxvideo.com/jvchat") {
/* Open Sans */
@font-face {
  font-family: "Open Sans";
  font-style: normal;
  font-weight: 400;
  src: local("Open Sans"), local("OpenSans"), url(http://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format("woff");
}

/* BG */
body{background:#444 url(http://image.noelshack.com/fichiers/2013/23/1370789578-1362915309-background.jpg) fixed center bottom repeat-y!important}

/* La page */
#page{margin-bottom:17px;box-shadow: 0px 0px 10px #000;}

/* Les éléments cachés */
#footer,#my,#new_header,#banner_jv  {display: none;}

/* Onglets */
#tabs li {font: 700 10px Open Sans!important; border-radius: 2px; border: 1px solid #949494!important; background: #F1F1F1!important; color: #5F5F5F!important; box-shadow: 0px 1px 2px #494949!important;}
#tabs li.tab_visible { background: #FF9696!important; border: 1px solid #FD5252!important; color: #B40A0A!important; }

/* Erreur */
#error_message { background: #444!important; border: 1px solid #292929!important; border-radius: 2px!important; padding: 15px!important; color: #FFF!important; text-shadow: 1px 0px 1px #000!important; font: 12px Open Sans!important; }
#error_message small { color: #FFF!important }

/* Zone de chat */
.chat big[id^="tab_big"] { display:block; padding-bottom: 20px!important; }
#chat1 ul { margin-bottom: 7px; }
#chat1 li { font: 11px Open Sans!important; border: 1px solid #A3A3A3!important; background: rgba(213, 213, 213, 0.25); margin: -1px; padding: 5px; }
.chat big { color: #5C5C5C!important; text-shadow: -1px -1px 1px #D1D1D1!important; font: 700 33px Open Sans!important; letter-spacing:0!important; }
section { padding: 15px 14px!important; margin: 0 -10px!important; }
#chat1 li:hover { text-decoration:none!important; background: rgba(177, 177, 177, 0.51)!important; }
.msg .pseudo a .text { font: 700 13px Calibri!important; }
.msg .pseudo a:hover { text-decoration:none!important; }
.msg .message{position:relative;}
.msg1 .message {background: #FFF4F4!important;border: 1px solid #FFBEBE!important;box-shadow: none!important;}
.msg0 .message {background: #F5F5F5!important;box-shadow: none!important;border: 1px solid #DBDBDB!important;}
.pseudo.myself a .text { color: #09F!important; }
.msg .date { font: 11px Open sans!important;margin: -6px -6px 0 0!important;background: #8F8F8F!important;border: 1px solid #7C7C7C!important;color:#FFF!important; border-radius:0!important }
.msg .date span { font: 11px Open sans!important; color: #FFF!important; } 
.msg .pseudo a {font:700 13px Calibri!important;}

/* Zone de réponse */
#post{position:relative;border-top:1px solid #5C5656!important;height:115px!important;background:url(http://image.noelshack.com/fichiers/2013/30/1375005332-yveltal.png)!important;margin:-10px!important;padding: 5px 5px 42px 5px!important;}
#area{margin-left:-9px!important;border: 1px solid #B4B0B0!important;font-weight: 700!important;max-width: 690px!important;}
#area:focus{outline:none!important;box-shadow:none!important}
#options{position:absolute;right:-1px;bottom:-1px;border: 1px solid #292929!important;padding: 5px 3px 5px 14px!important;background:#FFF!important}
#options a {font:11px Open Sans!important; padding: 0 0 0 10px!important; background: url(http://image.jeuxvideo.com/css_img/defaut/sommaire/sommaire_puces.png) no-repeat left .4em!important;}
#options a:hover {text-decoration:none!important;color:#CF3131!important; }
#send,.bbtn{background: #3A3A3A!important; color: #FFF!important; font: 700 13px Calibri!important; border: none!important; border-bottom: 1px solid #1A1919!important; border-radius: 3px!important; padding: 4px 14px 4px 14px!important; text-shadow: 0px 0px 3px #000!important;}
#send:focus, #send:hover, .bbtn:focus, .bbtn:hover {background: #303030!important; border-bottom: 1px solid #000!important;}
}
