css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("redditp.com") {

#prevButton{   margin-left: -10px !important; }
#nextButton{   margin-right: -10px !important; }
#prevButton, #nextButton{  /* opacity: .01 !important;  */ opacity: 0 !important; 
                                                        height: 85% !important;
                                                        width: 15% !important;
                                                        margin-top: -24% !important; }

/* #prevButton:hover, #nextButton:hover{  opacity: .8 !important; } */
#controlsDiv, #titleDiv{ background: none !important; }
#controlsDiv{ margin-left: -10px !important;
              margin-bottom: -15px !important; }
.nbmenu{width: 370px !important; }

.nbmenu, .numberButtonList{ background: rgba(0,0,0,.5) !important; }

#navboxTitle, #navboxSubreddit{     text-shadow: #000 0 0 1px, #000 0 0 2px, #000 0 0 3px  !important; 
                  opacity: .4 !important;  }
#navboxTitle:hover, #navboxSubreddit:hover{    opacity: 1 !important; }

.fadeOnIdle .collapser{    margin-top: 45% !important;    }
.collapser{  height: 70px !important;    width: 60px !important;
             font: 40pt sans-serif !important;
             text-shadow: #000 0 0 1px, #000 0 0 2px, #000 0 0 3px  !important;
             border: 0 !important; opacity: .6 !important; }
.collapser:hover{  opacity: 1 !important; }
.active{           box-shadow: none !important; }
.numberButtonList{max-height: 70px !important; }
}
