css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("storynexus.com") {
  .game #navbar { position: absolute !important; bottom: 0 !important; }
  .game #navbar .sub-menu { bottom: 30px !important; }
  .game #footer { margin: 0 !important; }

  #MajorLaterals img {
    width: 75px !important;
    height: 98px !important;
    display: block !important;
    margin: 0 auto 6px !important;
  }
  #MajorLaterals br { display: none !important; }
  
  #MinorLaterals img,
  #MidPanelQualities img,
  #inventory .inv-item img,
  #storylet-qualities img {
    width: 30px !important;
    height: 39px !important;
  }
  
  #inventory .inv-item { width: 30px !important; }

  #decks-intro, #hand-intro { margin-bottom: 5px !important; }
  #decks-list li { padding-bottom: 0 !important; }
  #hand-list { padding-top: 5px !important; padding-bottom: 0 !important; }
  
  #storylet-choices footer .challenge-desc { padding-bottom: 0 !important; }
  #storylet-choices .row.post-choice { padding: 0 10px !important; }

  .mini-share-btn { display: none !important; }
}
