css:
@-moz-document url-prefix("http://www.poneyvallee.com/forum"){

body {
    background : #FFFFFF url("http://img11.hostingpics.net/pics/519919fon.jpg") center fixed !important;
    background-size: 100% !important;
    color : #444444 !important;
}
.left, .right, .center {
    background: none !important;
}

#brdheader {
    background: rgba(150,100,140,0.4) url("http://img11.hostingpics.net/pics/431885bandeau2.gif") no-repeat right top !important;
    background-size : auto 150px !important;
    border : 2px #500000 solid !important;
    margin-top: 5px !important;
}

#brdmenu a:link { color: maroon !important;}
#brdmenu a:visited { color: maroon !important;}
a:link { color: maroon !important;}
a:visited { color: maroon !important;}
a:hover { border-bottom : black 2px solid !important;}

#brdmenu {
    background: -moz-linear-gradient(left, black, black, darkcyan, white, yellow) repeat fixed !important;
    background: -webkit-linear-gradient(left, rgba(200,200,200,1), rgba(150,100,140,0.4), rgba(150,100,140,0.4), rgba(50,50,50,1)) !important;
    border : 2px #500000 solid !important;
}

#brdmenu a{
    color : black !important;
    border-bottom: 1px black solid !important;
}

#brdmenu a:hover{
    color : black !important;
    border-bottom: black dotted 2px !important;
}

#brdwelcome li {
  	background: transparent !important;
    color : black !important;
}

#brdwelcome li a:hover{
    color : black !important;
    border-bottom : 1px dotted black !important;
}

h2 {
    background: -moz-linear-gradient(left, white, maroon, black) repeat fixed !important;
    background: -webkit-linear-gradient(left, white, maroon, black) repeat fixed !important;
    color : black !important ;
    border : 2px black solid !important;
    border-bottom : none !important ;
}

tbody h3 a {
    border-bottom : 1px #500000 solid !important ;
}

.tclcon {
    color : black !important ;
}
.tclcon a{
    color : maroon !important ;
}
.tclcon a:hover{
    color : black !important ;
    border-bottom : black 2px dotted !important ;
}

.tc2, .tc3 {
    color : black !important ;
}

.tcr {
    color : black !important ;
}
.tcr a {
    color : maroon !important ;
}
.tcr a:hover {
    color : black !important ;
    border-bottom : black 2px dotted !important ;
}

#brdstats .box, #brdfooter .box {
    background: rgba(150,100,140,0.4) url(http://img15.hostingpics.net/thumbs/mini_649244Sanstitre1.gif) no-repeat center  !important;
    border : 2px #500000 solid !important;
    color : black !important ;
}
#brdstats .box a, #brdfooter .box a{
    color : maroon !important ;
    border-bottom : none !important;
}
#brdstats .box a:hover, #brdfooter .box a:hover{
    color : black !important ;
    border-bottom : black 2px dotted !important;
}

.icon.inew {
    background:transparent url("http://img15.hostingpics.net/pics/700869new.png") left no-repeat !important;
    height : 50px !important; 
    margin-right : 20px  !important; 
}
.icon {
    background:transparent url("http://img15.hostingpics.net/pics/639050nonew.png") left no-repeat !important;
    height : 50px !important; 
    margin-right : 20px  !important; 
}

.blocktable .box {
    background: rgba(150,100,140,0.4) !important;
    border : 2px #500000 solid !important;
}

thead th, .arrondis_bas {
    background: rgba(150,100,140,0.4) !important;

}

.isticky .tclcon a {
    border-bottom : 1px solid black !important;
}
.isticky .tclcon a:hover {
    border-bottom : 2px dotted black !important;
}

DIV.blockpost DIV.box, DIV.postright, DIV.postfootright {background : transparent !important;}
DIV.postright, DIV.postfootright {BORDER-LEFT-COLOR: transparent !important;}
DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {background : transparent !important;}

.usertitle {
    color : black !important;
}
.postleft dt a{
    color : maroon !important;
    border-bottom: black 1px solid !important;
}

.postleft dt a:hover{
    color : black !important;
    border-bottom: black 2px dotted !important;
}

.linkst a{
    color : maroon !important;
    border-bottom: 1px solid black !important;
}
.linkst a:hover{
    color : black !important;
    border-bottom: 2px dotted black !important;
}

DIV.blockpost H2 SPAN.conr {COLOR: white !important;}

DIV.postright {
    BORDER-LEFT: solid 198px transparent !important;
}
.box, .blockpost {
    background : rgba(150,100,140,0.4) !important;
}
.blockpost, .box {
    border : 1px #500000 solid !important;
}
.blockpost a {
    color : maroon !important ;
    border-bottom : 1px solid black !important ;
}
.blockpost a:hover {
    color : black !important ;
    border-bottom : 2px dotted black !important ;
}

.pun {
color : black !important;
}

tbody td, thead th {
    background: transparent !important;
    border : none !important ;
    border-top : 1px solid black !important ;
}
}
