css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("icanhascheezburger.com")
{
   /* remove unnecessary elements*/
   #floattop,
   #footer,
   #othersites,
   #headerlogo,
   #leaderboard
   .socmarks,
   .dynamicsidebar,
   .skyscraper,
   .pane1,
   .toprightad,
   .sidebarbody,
   .sideblock h3,
   br
   {
      display: none !important;
   }
   
   /* main panel with lolcats */
   .pane2
   {
      width: 500px !important;
      margin: 0 auto !important;
      float: none !important;
      overflow: visible !important;
   } 
   
   /* main menu */
   #header
   {
      margin: 10px auto 0 auto !important;
      float: none !important;
      height: 16px !important;
      width: 960px !important; 
   }

   /* managing sideblocks */
   .sideblock
   {
      border: 0 !important;
      position: relative !important;
      left: 8px !important;
   }
    
   #pane3
   {
      min-height: 20px !important;
      position: absolute !important;
      top: 73px !important;
   } 
   
   .sideblock,
   #pane3,
   .panemain
   {
      width: 248px !important;
      margin: 0 !important;
      padding: 0 !important;
   }
   
   /* input forms, subscribe and search */
   .sideblock form
   {
      float: left !important;
      width: 217px !important;
      margin: 0 0 5px 0 !important;
   }
   
   /* rss small icon */
   .sideblock p a img
   {
      width: 16px !important;
      height: 16px !important;
      float: left !important;
      margin: 2px 0 0 5px !important;
   }

   /* other links and texts */
   .sideblock p
   {
      font-size: 0 !important;
   }

   #pane3 p
   {
      margin:0 !important;
      padding:0 !important;
   }
   
   .socmarklast h3
   {
      margin: 5px 0 !important;
   }
   
   .socmarklast textarea
   {
      width: 500px !important;
      border: 1px solid #000 !important;
   }
   
   /* input fields */
   .sideblock form input[name="email"],
   .sideblock form input[name="s"]
   {
      float: left !important;
      height: 18px !important;
      border: 1px solid #000 !important;
      padding: 0 2px !important;
      margin: 0 !important;
      width: 120px !important; 
   }
   
   /* buttons */
   .sideblock form .button
   {
      float: left !important;
      height: 20px !important;
      border: 0 !important;
      padding: 0 5px !important;
      font-weight: bold !important;
      margin: 0 0 0 5px !important;
   }
}
