css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

                                /* full-width */
                                
.dashboard { display: none !important; }
.content-main { width: 837px !important; }
.profile-card.profile-header {
    margin-left: auto !important;
    margin-right: auto !important;
    float: none !important;
}

    body {
    background-color: rgba(0, 0, 0, 1) !important;}
    
    .wrapper-profile .profile-card.profile-header .profile-header-inner {
    background-color: rgba(255, 255, 255, 1) !important;}
    
    .module {
    background-color: rgba(255, 255, 255, 1) !important;}
    
    .global-nav {background: linear-gradient(to bottom, rgba(253,253,253,0.9), rgba(253,253,253,0.95)1%, rgba(253,253,253,0.9), rgba(253,253,253, 0.8) 95%  , rgba(253,253,253,0.7) 99%  ) !important;}
    
    .tweet {
    border-radius: 1px !important;
    border-color: black !important;
    border: 5px  !important;
    }
    
    div.module.signup-call-out, div.view-more-container, div.tweet.permalink-tweet.js-actionable-user.js-actionable-tweet.with-social-proof.opened-tweet.animating,
    div.simple-tweet.tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.with-social-proof,
    div.simple-tweet.tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable,
    div.original-tweet.tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.js-original-tweet,
    div.simple-tweet.tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.opened-tweet.animating,
    div.tweet.permalink-tweet.js-actionable-user.js-actionable-tweet.with-social-proof.opened-tweet,
    div.tweet.original-tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.js-original-tweet.with-social-proof,
    div.tweet.original-tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.js-original-twee,
    div.stream-end-inner,.stream-end-inner,.stream-loading 
    div.replies.js-conversation-replies,  
    .tweet-form.condensed div.rich-editor,
    .inline-reply-tweetbox.swift .tweet-box {
    background: linear-gradient(to top, rgba(251, 251, 251,0.8), rgba(251, 251, 251, 0.9) ,rgba(251, 251, 251, 0.8), rgba(251, 251, 251, 0.9),  rgba(0, 0, 0, 0.2) 95% , rgb(0, 0, 0) 99% ) !important;}
    
    
    
   
#page-container {background-color:rgba(0, 0, 0, 0.8) !important}

/*.wrapper-profile .profile-card.profile-header .profile-header-inner {background:000 !important}*/

.flex-module.profile-banner-footer.clearfix {background:000 !important}

   div.flex-module {
        background:rgba(0, 0, 0, 0.8) !important
    }
    
    div.holding.name
    input
    
   {
        background:rgba(0, 0, 0, 0.8) !important
    }
    div.holding.email
    input.email-input
    {
        background:rgba(0, 0, 0, 0.8) !important
    }
    div.holding.password
    input
    
   {
        background:rgba(0, 0, 0, 0.8) !important
    }
    
    a.list-link.js-nav {background:rgba(0, 0, 0, 0.8) !important;}
    
    div.header-inner { background:rgba(0,0,0,0.05) !important
    }
    
    
    a,li {color:#000 !important;}
    
    

    b {color:black !important;}
    s {color: black !important;}
    span {color: black !important; /*font-weight: bold !important;*/}
    p {color: black !important; font-size: 90% !important; font-weight: 900 !important;
    text-shadow: 2px 2px 4px #fff, 1px 1px 4em black !important;
    }
    
    .spinner {display:none !important;}
    
    .inline-reply-tweetbox .tweet-box {color: black !important;}
    
    
    .geo-picker .dropdown-menu p {color: black !important;}
    
    .profile-card .stats {color: black !important; font-size: 90% !important; font-weight: 900 !important;
    text-shadow: 2px 2px 4px #fff, 1px 1px 4em black !important;
    }
    
    .wrapper-profile .profile-card.profile-header .profile-header-inner {color: black !important; font-size: 135% !important;
    font-weight: 900 !important;
    text-shadow: 2px 2px 4px #fff, 1px 1px 4em black !important;
    }
    
    
    .profile-card.profile-header .bio-container {font-size: 105% !important;
    }
    
    /*.wrapper-profile .profile-card.profile-header .profile-header-inner {
    background-image: url("https://si0.twimg.com/profile_banners/61033129/1362011257/web") !important;
}*/
    .wrapper-profile .profile-card.profile-header .profile-header-inner {
    background-attachment:fixed !important;
    }
    
}
