css:
/* name: Talk Cat Melon fujiwarashinya.com ja jp */
@-moz-document url-prefix("http://www.fujiwarashinya.com/talk/"){
/*- generic font attr to default -*/
* {font-family: none !important; font-size: auto !important;}

/*- page relayout -*/
body {background: url("http://www.fujiwarashinya.com/img/img_catmelon.jpg") 0% / 25% fixed;} /* add background image */
#container { /* article container use opacity */
 width: inherit !important; min-width: 640px;
 background: #EFEFEF; opacity: 0.9;
 }
#container h1, #container h2 { /* page title and blog title restyling */
 width: auto !important;  height: auto !important;
 padding: 0.5em !important;
 font-weight: auto !important;
 }
#container h1 {color: black !important; text-shadow: 0 0 4px black;} /* page title decorate */
#container #center {width: 100% !important; margin-right: -165px;} /* columns requid relayout */
#container #right /* right col */
 {width: 165px !important; border: none !important; background: #EFEFEF !important;}
#center>* { /* main col */
 width: auto !important; margin-right: 170px; /* 2013/03/03 */
  box-shadow: 1px 1px 8px black;
 }

/*- main article -*/
#center p {margin: 0 !important; display: none !important;}
.columnview br {line-height: 0.5em;} /* modify redundant line-blreak */

/*- rihgt sidebar (calender) -*/
#right p.mainmenu {
 width: 100% !important;
 min-height: 1.4em; /* 2013/03/03 */
 margin: 0 !important; padding: 0 !important;
 border: none !important;
 background: none !important;
 }
#right #timezone table {border: 1px solid gray !important;}
}
