css:
@-moz-document domain(tumblr.com) {
    body {
    background-color: #fdf3ea;
    color:#565656 !important;
    font-size:12px;
    line-height:1.2;
    margin:0;
    padding:0;  
    background: url('http://i.imgur.com/qu7QiPp.png') bottom right fixed #fdf3ea!important;
    background-repeat: no-repeat!important;
    background-attachment: fixed;
    }
     
     
    *{font-family: Century Gothic, Century Gothic, Serif !important;}
     
     
    ol#posts li blockquote,
     
     
     
    body.mceContentBody blockquote {
     
     
     
    margin-left: 0 !important;
     
     
     
    margin-right: 0 !important;
     
     
     
    padding-left: 10px !important;
     
     
     
    border-width: 4px !important;
     
     
     
    border-color: #a8bccf !important;
     
     
     
    }
     
     
     
    ol#posts li blockquote blockquote,
     
     
     
    body.mceContentBody blockquote blockquote {
     
     
     
    border-color: #839aaf !important;
     
     
     
    }
     
     
     
    ol#posts li blockquote blockquote blockquote,
     
     
     
    body.mceContentBody blockquote blockquote blockquote {
     
     
     
    border-color: #6b7d8f !important;
     
     
     
    }
     
     
     
    ol#posts li blockquote blockquote blockquote blockquote,
     
     
     
    body.mceContentBody blockquote blockquote blockquote blockquote {
     
     
     
    border-color: #4c5e6f !important;
     
     
     
    }
     
     
     
    ol#posts li blockquote blockquote blockquote blockquote blockquote,
     
     
     
    body.mceContentBody blockquote blockquote blockquote blockquote blockquote {
     
     
     
    border-color: #36434f !important;
     
     
     
    }
     
     
     
    #dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
     
        background: rgba(0,0,0,0.2) !important;
     
      }
     
     
     
    #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
     
        background: rgba(0,0,0,0.2) !important;
     
        -webkit-border-radius: 20px;
     
        -moz-border-radius: 20px;
     
        border-radius: 20px;
     
        margin: auto;
     
        padding: 20px 20px;
     
      }
     
     
     
    h1 {
     
    color: #333333;
     
    text-shadow: 0 0 3px #ffffff;
     
    }
     
     
     
    .no_posts_found, h1.dashboard_header {
     
    color: #666666;
     
    }
     
     
     
    img#content_top, img#content_bottom {
     
    display: none;
     
    }
     
     
     
    #right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
     
        background: url(http://i.imgur.com/qu7QiPp.png);
     
      }
     
     
     
    #right_column .dashboard_nav_item {
     
        background: url(http://i.imgur.com/qu7QiPp.png) repeat-x;
     
        background-attachment: initial;
     
        background-position-x: 50%;
     
        background-position-y: 100%;
     
        background-origin: initial;
     
        background-clip: initial;
     
        background-color: initial;
     
    }
     
     
     
    #right_column .dashboard_nav_item #hide_radar {
     
    background-image: url(http://i.imgur.com/qu7QiPp.png);
     
    background-repeat-x: no-repeat;
     
    background-repeat-y: repeat;
     
    background-attachment: initial;
     
    background-position-x: 0%;
     
    background-position-y: 0%;
     
    background-origin: initial;
     
    background-clip: initial;
     
    background-color: #000000;
     
    }
     
     
     
    #right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
     
    background: rgba(0,0,0,0.5);
     
    }
     
     
     
    #nav .nav_item.active {
     
        background: rgba(50,0,0,0.5) !important;
     
      }
     
     
     
    #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
     
        background: rgba(50,0,0,0.5) !important;
     
      }
     
     
     
    ol#posts li.notification.alt {
     
        background-color: rgba(0,0,0,0.5);
     
    }
     
     
     
    ol#posts li.notification.first_notification {
     
        background-color: rgba(0,0,0,0.8);
     
    }
     
     
     
    ol#posts li.notification {
     
        background-color: rgba(0,0,0,0.8);
     
        border-bottom: 1px solid #000000;
     
    }
     
     
     
    ol#posts li.notification.last_notification {
     
        border-bottom: 1px solid #000000;
     
    }
     
     
     
    ol#posts li.post .permalink {
     
        background: rgba(50,0,0,0.9);
     
    }
     
     
     
    #right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
     
    background: rgba(0,0,0,0.8);
     
    border: solid #000000;
     
    }
     
     
     
    ol#posts li.post .post_avatar{
     
    border-bottom: 0px solid #0A2939;
     
    }
     
     
     
    #right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
     
    border-top: 1px solid #000000;
     
    border-bottom: 1px solid #000000;
     
    }
     
     
     
    #right_column a#dashboard_switch_blog_menu_current:hover {
     
    background: url(http://i.imgur.com/qu7QiPp.png);
     
    }
     
     
     
    #right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {
     
    background: url(http://i.imgur.com/qu7QiPp.png);
     
    }
     
     
     
     
    #right_column .radar .content{background:transparent!important}
    #right_column .radar .content .mask{background:transparent!important}
    #right_column .controls_section .selected{background:rgba(0,0,0,0)!important;background-image:none!important;border-top:1px solid rgba(0,0,0,0)!important;}
    #right_column .controls_section .recessed{background-color:#e6d4c5!important;-webkit-box-shadow:none!important;-moz-box-shadow:none!important;box-shadow:none!important;}
    #right_column .controls_section .gradient{background:none!important;border-top:0px!important;}
    #right_column .controls_section .recessed:active{border-top:1px solid rgba(0,0,0,0)!important}
     
     
    form.dashboard_options_form {
     
    background-color: rgba(0,0,0,0.7);
     
    border-bottom: 1px solid #000000;
     
    border-bottom-left-radius: 10px 10px;
     
    border-bottom-left-radius: 10px 10px;
     
    border-bottom-right-radius: 10px 10px;
     
    border-bottom-right-radius: 10px 10px;
     
    border-top-left-radius: 10px 10px;
     
    border-top-left-radius: 10px 10px;
     
    border-top-right-radius: 10px 10px;
     
    border-top-right-radius: 10px 10px;
     
    color: #ffffff;
     
    display: block;
     
    font-size: 13px;
     
    margin-bottom: 25px;
     
    padding: 15px 22px;
     
    }
     
     
     
    form.dashboard_options_form .option_container {
     
    border-top: 1px solid #000000;
     
    }
     
     
     
    .no_posts_found, h1.dashboard_header {
     
    color: #333333;
     
    }
     
    #dashboard_index, #dashboard_tags, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue,#dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_members, #main_tag_beta, #dashboard_upload_static_file, #main_about, #main_doc_page, #main_themes, #main_meetups, #main_jobs, #main_directory_page, #dashboard_settings, #dashboard_goodies {
     
    background: url('http://i.imgur.com/qu7QiPp.png') bottom right fixed #fdf3ea!important;
    background-repeat: no-repeat!important;
    background-attachment: fixed;
     
     
    }
     
     
     
    #dashboard_edit_post{
     
    background-color: #77acb2 !important
     
    }
     
     
     
    #left_column #posts .post .permalink {
     
    background-color:#ccc !important;
     
    -moz-border-radius: 0px 11px 0px 0px !important;
     
    -webkit-border-radius: 0px 11px 0px 0px !important;
     
    }
     
     
     
    #left_column #posts .post .post_avatar,
     
    #left_column #posts .post .sub_avatar .sub_avatar_inner {
     
    background-color: #fbfbfb !important;
     
    border-bottom:0px !important;
     
    }
     
     
     
    #left_column #posts .post{
     
    border-bottom:0px #1f0b1b solid !important
     
    }
     
     
     
    #nav .nav_item.active,
     
    #nav .nav_item .nav_menu,
     
    #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
     
    background-color:#fff !important;
     
    background-image:none !important;
     
    border:0px !important;
     
    background: rgba(256,256,256,0.3) !important;
     
    font-family: Georgia !important;
     
    font-style: italic !important;
     
    }
     
     
     
    #nav .nav_item {
     
    color:#777 !important;
     
    font-family: Georgia !important;
     
    font-style: italic !important;
     
    }
     
     
     
    #nav .nav_item:hover {
     
    color:#999 !important;
     
    }
     
     
     
    #nav .nav_item .nav_menu {
     
    border-bottom:0px !important
     
    }
     
     
     
    #nav .nav_item .nav_menu a {
     
    color:#555 !important;
     
    border-top:0px !important;
     
    }
     
     
     
    #nav .nav_item .nav_menu a:hover {
     
    color:#555 !important;
     
    background: rgba(256,256,256,0.4) !important;
     
    }
     
     
     
    #footer{
     
    color:#fff !important;
     
    background:transparent !important;
     
    background-image:none !important;
     
    text-shadow:none !important;
     
    font-family: Georgia !important;
     
    }
     
    #footer a{color:#fff !important;}
     
     
     
    .no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {
     
    color:#ccc !important
     
    }
     
     
     
    #right_column .dashboard_nav_item {
     
    border-bottom:0px solid rgba(0,0,0,0.5) !important;
     
    background-image:none  !important
     
    }
     
                        #right_column .dashboard_nav_item a,
     
                        #right_column .dashboard_nav_item ul.dashboard_subpages,
     
                        #right_column .dashboard_nav_item ul.dashboard_subpages li a {
     
                            color:#555 !important;
     
    }
     
     
     
    #right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
     
                            color:#555 !important ;
     
                        }
     
     
     
    #right_column .dashboard_nav_item .dashboard_nav_title{color:#555 !important}
     
    #right_column .dashboard_nav_item .dashboard_nav_title a{color:#555 !important}
     
     
     
    #right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
     
                            background: none !important;
     
                        }
     
     
     
    #show_radar a, #hide_radar a,
     
                            #hide_recommended_blogs a, #show_recommended_blogs a {
     
                                color:#555 !important;
     
    background: none !important;
     
                            }
     
    #dashboard_controls_radar_media{background: none !important;}
     
    #dashboard_controls_suggested_blogs{background-color: rgba(256,256,256,1) !important; border:0px !important}
     
    #suggested_blog_avatar_1,#suggested_blog_avatar_2,#suggested_blog_avatar_3{border:0px !important}
     
    .dashboard_controls_suggested_blog{border:0px !important}
     
     
     
    #left_column #posts .notification {
     
                            background-color:#fbfbfb !important;
     
                            color:#667 !important;
     
                            border-bottom-color: #eee !important
     
                        }
     
     
     
    #left_column #posts .notification .block {
     
                                color:#333 !important;
     
                            }
     
     
     
    #left_column #posts .notification.alt {
     
                            background-color: #f7f7f7 !important;
     
                        }
     
    #left_column #posts .notification.last_notification,
     
                        #left_column #posts .notification.single_notification {
     
                            border-bottom:0px !important;
     
                        }
     
    #left_column #posts .notification a {
     
                            color:#565656 !important;
     
                            border-bottom:0px !important;
     
                        }
     
    #left_column #posts .notification blockquote {
     
                            border-left-color:#eee !important;
     
                        }
     
     
     
    #left_column #posts .notification .avatar {
     
                            border-color:transparent !important;
     
                            background-color:#fbfbfb !important;
     
    opacity:0.5 !important;
     
                        }
     
     
     
    #left_column #posts .notification .avatar:hover{
     
    opacity:1 !important;
     
    border-bottom:0px !important;
     
                        }
     
     
     
    #dashboard_switch_blog_menu{
     
    background-color: rgba(256,256,256,0.8) !important;
     
    text-shadow:none !important;
     
    border:0px !important;
     
    font-family: Georgia !important;
     
    font-style: italic !important;
     
    }
     
    #dashboard_switch_blog_menu_header, #dashboard_switch_blog_arrow_and_divider,.dashboard_switch_blog_menu_item,#dashboard_switch_blog_menu_footer {
     
    text-shadow:none !important;
     
    border:0px !important;
     
    color:#fff !important;
     
    font-family: Georgia !important;
     
    font-style: italic !important;
     
    }
     
    .dashboard_switch_blog_menu_item a{
     
    border:0px !important;
     
    color:#ccc !important;
     
    }
     
    .dashboard_switch_blog_menu_item:hover{
     
    background-color: rgba(256,256,256,0.5) !important;
     
    color:#555 !important;
     
    }
     
    #dashboard_switch_blog_menu_footer{
     
    background-color: rgba(256,256,256,0.6) !important;
     
    }
     
     
     
    #new_post_notice_container{
     
    color:#fff !important;
     
    background-image: none !important;
     
    }
     
    #new_post_notice_container a{
     
    color:#fff !important;
     
    }
     
     
     
    #dashboard_switch_blog_menu_current,
     
    #dashboard_switch_blog_arrow_and_divider,
     
    #dashboard_switch_blog_arrow_current
     
    {
     
    background-image:none !important
     
    }
     
    .notification_type_icon{
     
    display:none !important
     
    }
     
     
     
     
     
    h1,h2,h3,h4,h5,h6{color:#eee !important}
     
     
     
    .new_post_label{
     
    text-transform: lowercase !important;
     
    font-family:Georgia !important;
     
    font-size: 12px !important;
     
    font-style:italic !important;
     
    margin-top:8px !important;
    letter-spacing:4px !important
     
    }
     
     
     
    ol#posts li.post.new_post {
     
    background: #ffffff;
     
    }
     
     
     
    img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
     
        width:0;
     
        height:70px;
     
        padding-right:250px;
     
        visibility: hidden;
     
    }
     
     
     
    .sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}
     
     
     
    #right_column .dashboard_nav_item ul.dashboard_subpages li a {
     
    color:#555 !important;
     
    text-shadow: none !important;
     
    margin:0px !important;
     
    }
     
     
     
    #right_column .dashboard_nav_item ul.dashboard_subpages li a:hover{
     
    color:#cdcdcd !important;
     
    text-shadow: none !important;
     
    }
     
    #left_column #posts .post .post_avatar{-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important;}
     
    #left_column #posts .post .sub_avatar_inner {
     
    -moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;
     
    }
     
    #dashboard_switch_blog_menu_current{color:#676767 !important;
     
    font-family: Georgia !important;
     
    font-style: italic !important;
    text-shadow:none !important
     
    }
     
     
     
     
     
     
     
    #right_column .dashboard_nav_item .dashboard_nav_title,
     
                        #right_column .dashboard_nav_item .dashboard_nav_title a,
     
                        #right_column .dashboard_nav_item a,
     
                        #right_column .dashboard_nav_item ul.dashboard_subpages,
     
                        #right_column .dashboard_nav_item ul.dashboard_subpages li a {
     
                            color:#777 !important;
     
    font-family: Georgia !important;
     
    font-style: italic !important;
     
    }
     
    #right_column .dashboard_nav_item ul.dashboard_subpages li{margin-bottom:10px !important;}
     
    #right_column .dashboard_nav_item ul.dashboard_subpages li a {
     
    background-color:rgba(256,256,256,0.6) !important;
     
    color:#888 !important;
     
    -moz-border-radius: 10px !important;
     
    -webkit-border-radius: 10px !important;
     
    margin:0px !important;
     
    padding:4px  !important
     
    }
     
    #right_column .dashboard_nav_item ul.dashboard_subpages li a:hover{
     
    background-color:rgba(256,256,256,0.8) !important;
     
    color:#666 !important;
     
    -moz-border-radius: 10px !important;
     
    -webkit-border-radius: 10px !important;
     
    padding:4px  !important
     
    }

}
