css:
@-moz-document domain("facebook.com") {
body{
background: url("http://img.ihere.org/uploads/40862cda43.jpg") #fff top center fixed !important;
}
body>#blueBar{ background: none !important;}
.jewel, .jewelBox{ border: none !important; }
.jewelBox{ background-color: rgba(255,255,255,0.9) !important; }
#headNavOut{ background: none !important; border: none !important }
#pageNav a { color: #111 !important; }
#pageNav a:hover { color: #333 !important; background: none !important; }
#navAccount{border: none !important;}
#navAccount ul{ background-color: rgba(255,255,255,0.9) !important;  border: none !important; }
#contentCol,#left_column,#right_column{ background-color: rgba(255,255,255,0.4) !important;  border: none !important;}
.inputtext,.add_comment_text,.uiMorePager, .box_header{ background-color: rgba(255,255,255,0.5) !important; }
.ufi_section, .photo_table, .photo_container{background: none !important; border: none !important;}
#sidebar_ads, #pagelet_adbox, #ego{
display: none !important;
}
#photoborder{ background-color: rgba(255,255,255,0.4) !important;  border: none !important; padding: 10px 0;}
.profile_sidebar_ads{ height: 30px; }
#pagelet_chbox{border: none !important;}
#pageFooter, #rightCol, #contentArea, .UIStory, #mainContainer, .profile_top_wash{background: none !important; border: none !important;}

#contentCurve{ display: none !important; }
.gray_box {background-color: rgba(255,255,255,0.4) !important;  border: none !important;}
#pagelet_tab_content{ padding: 10px; }
.uiSideNav a { border: none !important; }
.uiSideNav li.selected a{background-color: rgba(255,255,255,0.4) !important;}
.uiSideNav a:hover{background-color: rgba(255,255,255,0.4) !important;}

#headNav {
background-color: #000000;
border: 1px solid #244866;
border-bottom: 0;
height: 30px;
margin-left: 180px;
}

#pageNav a {
color: #193f4d !important;
}

a {
cursor: pointer;
color: #193f4d;
text-decoration: none;
}

#pageLogo a {
background: url("http://i46.tinypic.com/2dhvbd4.png") no-repeat center center !important;
background-image: url(http://i52.tinypic.com/ztfbx1.png);
background-repeat-x: no-repeat;
background-repeat-y: no-repeat;
background-attachment: initial;
background-position-x: 50%;
background-position-y: 50%;
background-origin: initial;
background-clip: initial;
background-color: initial;
}

#fbNotificationsJewel a.jewelButton {
background-image: url(http://i53.tinypic.com/2yke0zk.png);
background-repeat: no-repeat;
background-repeat-x: no-repeat;
background-repeat-y: no-repeat;
background-position: -125px -87px;
background-position-x: -125px;
background-position-y: -87px;
}


#fbMessagesJewel a.jewelButton {
background-image: url(http://i53.tinypic.com/2yke0zk.png);
background-repeat: no-repeat;
background-repeat-x: no-repeat;
background-repeat-y: no-repeat;
background-position: -200px -87px;
background-position-x: -200px;
background-position-y: -87px;
}


#fbRequestsJewel a.jewelButton {
background-image: url(http://i53.tinypic.com/2yke0zk.png);
background-repeat: no-repeat;
background-repeat-x: no-repeat;
background-repeat-y: no-repeat;
background-position: -75px -87px;
background-position-x: -75px;
background-position-y: -87px;
}

#blueBar {
background-color: transparent;
}


#fbRequestsJewel .jewelButton {background: url("http://i795.photobucket.com/albums/yy232/DaedalusIcarusHelios/Friends-Gray.png") no-repeat center center !important;}

#fbRequestsJewel:hover .jewelButton, #fbRequestsJewel.hasNew .jewelButton {background: url("http://i795.photobucket.com/albums/yy232/DaedalusIcarusHelios/Friends.png") no-repeat center center !important;}

#fbMessagesJewel .jewelButton {background: url("http://i795.photobucket.com/albums/yy232/DaedalusIcarusHelios/Mail_Icon-gray.png") no-repeat center center !important;}

#fbMessagesJewel:hover .jewelButton, #fbMessagesJewel.hasNew .jewelButton {background: url("http://i795.photobucket.com/albums/yy232/DaedalusIcarusHelios/Mail_Icon.png") no-repeat center center !important;}


#fbNotificationsJewel .jewelButton {background: url("http://i795.photobucket.com/albums/yy232/DaedalusIcarusHelios/Earth-gray.png") no-repeat center center !important;}

#fbNotificationsJewel:hover .jewelButton, #fbNotificationsJewel.hasNew .jewelButton {background: url("http://i795.photobucket.com/albums/yy232/DaedalusIcarusHelios/Earth.png") no-repeat center center !important;}

.slim #blueBar {
border-bottom: none;
border-bottom-width: none;
border-bottom-style: none;
border-bottom-color: none;
-webkit-box-shadow: none;
height: 0px;
position: static;
}


.presence_menu_opts,#header, .LJSDialog, .chat_window_wrapper, #navAccount ul, .fbJewelFlyout, .uiTypeaheadView, .uiToggleFlyout { box-shadow: 0 0 3em #000; }

.UIRoundedImage, .UIFilterList > .UIFilterList_Title,  .dialog-title, .flyout, .uiTooltipWrap {box-shadow: 0 0 1em 1px #000;}

.extra_menus ul li:hover, .UIRoundedBox_Box, .fb_menu_link:hover, .UISelectList_Item:hover, .fb_logo_link:hover, .hovered, 


.fbChatBuddyListDropdown .uiButton:hover, .uiButton:not(.uiSelectorButton):hover, .fbPrivacyWidget .uiSelectorButton:not(.lockButton):hover, .uiButtonSuppressed:hover, .UIButton:hover, .UIActionMenu_Wrap:hover, .tabs li:hover, .ntab:hover, input[type="submit"]:not(.fg_action_hide):not(.stat_elem):not([name="add"]):not([name="actions[reject]"]):not([name="actions[accept]"]):not([value="Find Friends"]):not([value="Share"]):not([value="Maybe"]):not([value="No"]):not([value="Yes"]):not([value="Comment"]):not([value="Reply"]):not([type="Flag"]):not([type="submit"]):hover, .inputsubmit:hover, .promote_page:hover, .create_button:hover, .share_button_browser:hover, .silver_create_button_shell:hover, .painted_button:hover, .flyer_button:hover, .button:not(.close):not(.uiSelectorButton):hover, button:not(.as_link):hover, .GBSearchBox_Button:hover, .tagsWrapper {box-shadow: 0 0 .5em rgba(0,0,0,0.9), 0 0 1em 3px #99ccff, inset 0 0 2em #6699ff !important;}



#icon_garden, .list_select .friend_list {box-shadow: 0 0 3px -1px #000, inset 0 0 3px -1px #000;}

.bb .fbNubButton {box-shadow: inset 0 4px 8px #99ccff, 0 0 1em #000 !important;}
.bb .fbNubButton:hover {box-shadow: inset 0 4px 8px #99ccff, 0 .5em 1em 1em #99ccff !important;}

.fbNubFlyoutTitlebar {box-shadow: inset 0 4px 8px #99ccff;padding: 0 4px !important;}

#fb_menubar, .progress_bar_outer {box-shadow: inset 0 0 3px #000, 0 0 3em 3px #000;}
#presence_ui {box-shadow: 0 0 3em 1px #000}

#presence_applications_tab:hover, #buddy_list_tab:hover, #presence_notifications_tab:hover, .tab_handle:hover, .focused {box-shadow: 0 0 3px #000, inset 0 0 3px #000, 0 0 3em 5px #fff;}


.uiSideNavCount, .jewelCount {box-shadow: 0 0 1em 4px #99ccff}


.img_link:hover, .album_thumb:hover {box-shadow: 0 0 3em #99ccff;}

.shaded, .progress_bar_inner {box-shadow: inset 0 0 1em #99ccff;}

.UIPhotoGrid_Table .UIPhotoGrid_TableCell:hover .UIPhotoGrid_Image, #myphoto:hover, .mediaThumbWrapper:hover, .uiVideoLink:hover, .mediaThumb:hover, #presence.fbx_bar #presence_ui #presence_bar .highlight, .fbNubFlyout:hover, .hovercard .stage, #fbDockChatBuddylistNub .fbNubFlyout:hover  {box-shadow: 0 0 3em 5px #99ccff !important;}

.fbNubFlyout {box-shadow: 0 0 3em 5px #000 !important;}

.UIComposer_Attachment .UIButtond {box-shadow: none !important;}


.wsh #blueBar {
background-color: 
transparent;
min-width: 981px;
z-index: 300;
}

.wsh .slim #blueBar {
border-bottom: 0px solid 
transparent;
-webkit-box-shadow: 0 0px 0px 0px 
rgba(0, 0, 0, .52);
height: 37px;
position: relative;
}

.nsh .slim #blueBar {
background-color: 
transparent;
border-bottom: 0px solid 
transparent;
-webkit-box-shadow: 0 0px 0px 0px 
rgba(0, 0, 0, .52);
height: 37px;
min-width: 981px;
position: fixed;
z-index: 300;
}

.nsh .slim #blueBar.fixed_elem {
left: 0;
right: 0;
top: 0;
}

#headNav {
background-color: 
transparent;
border: 0px solid 
transparent;
border-bottom: 0;

}




/* --------------------------------------------------------------------- General ----------------- */

body.login_page,body.fbIndex,body.registration{ background-image:url(http://www.tumpakplastik.com/images/bg.jpg)!important; background-repeat:no-repeat!important;  background-position:center center;  width:100%!important;  height:100%!important;  overflow-y:auto!important;  overflow-x:hidden!important}

/* Center Login Bar - General*/
.fbIndex .loggedout_menubar_container, 
.login_page .login_page_interstitial, 
.registration .UIContentTopper, 
.registration .UIWindowShade{ min-width:0 !important;  z-index:-1 !important;  padding-top:10px!important;  padding-right:10px!important;  padding-bottom:10px!important;  top:40% !important;  left:50% !important;  background-color:rgba(255,255,255,.2)!important}

.fbIndex .loggedout_menubar_container:hover,
.login_page .login_page_interstitial:hover{
  opacity: 1!important;
}

/* Hide Crap */
.fbIndex #globalContainer #dropmenu_container, 
.fbIndex #globalContainer #content, 
.fbIndex #globalContainer #pageFooter, 
#SetAsHomepage_Callout, 
.login_page #pageFooter, 
.login_page .fb_logo.img, 
.login_page .localeSelectorList, 
.login_page div.rg3fbpz-tooltip .login_page div.fb_content, 
.login_page div.WelcomePage_MainSellCenter, 
.login_page div.WelcomePage_MainSell, 
.login_page #pageFooter, 
.login_page ul.welcome_locales, 
.login_page .signup_box, 
.login_page .linear_language, 
.login_page div#blueBar, 
.login_page div.WelcomePage_Container, 
.signup_bar_container, 
.registration div#blueBar, 
.registration #globalContainer #dropmenu_container, 
.registration #globalContainer #pageFooter{display:none !important}

.fbIndex .menu_login_container input[type="text"], 
.fbIndex .menu_login_container input[type="password"], 
.login_page #loginform input[type="text"], 
.login_page #loginform input[type="password"]{ padding:6px !important;  font-size:20px !important;  border:1px solid #424242 !important;  background:#fff!important;  font-family:Georgia!important;  vertical-align:middle!important;  width:60%!important;  background-color:rgba(229,229,229,.2)!important;  border:none}

/* Login Button */
.fbIndex label input, 
.login_page label input{padding:8px !important}

/* Float:None */
.fbIndex .loggedout_menubar_container .lfloat, 
.fbIndex .loggedout_menubar_container .rfloat, 
.login_page .lfloat{float:none !important}

.fbIndex .menu_login_container .login_form_label_field label, 
.fbIndex .menu_login_container td.html7magic label, 
.fbIndex .menu_login_container .login_form_label_field a, 
.login_page h2.uiHeaderTitle, 
.login_page .login_form_label, 
.login_page #loginform a, 
.login_page .uiInputLabel label, 
.login_page #persistent_login_text{color:black!important; font-size:11px; text-shadow:1px 1px 3px white!important; font-weight:bold!important}

/* --------------------------------------------------------------------- Login HOMEPage ----------------- */

.fbIndex .menu_login_container label{color:#424242 !important; margin:2px !important}

.fbIndex .menu_login_container input[type="text"], 
.fbIndex .menu_login_container input[type="password"]{width:90%!important}

/* Center Login form */
.fbIndex .loggedout_menubar_container{ position:fixed !important;  width:450px !important;  height:82px !important;  margin-top:-10px !important;  margin-left:-210px !important;  z-index:-1 !important;  opacity:0.8!important}

/* Others */
.fbIndex .loggedout_menubar{width:auto !important}
.fbIndex .loggedout_menubar_container .lfloat img, 
.fbIndex .loggedout_menubar_container .rfloat #login_form table{display:block !important; margin:0 auto !important}
.fbIndex .loggedout_menubar_container .lfloat img{display:block; margin:-70px auto 20px !important}

/* ---------------------------------------------------------------------------- Login Page ----------------- */

.login_page .loggedout_menubar_container{background:none!important; border:none!important}

.login_page .loggedout_menubar{margin-left:auto !important; margin-right:auto !important; width:400px !important; padding-top:0px !important}

.login_page #loginform a{font-weight:bold!important; color:#000!important}

.login_page .login_page_interstitial{padding:10px!important; margin-top:180px!important; border:none!important; opacity:0.8!important}

.login_page .login_page_interstitial:hover{opacity:1!important}

/* ------------------------------------------------------------------- SignUp Page ----------------- */

/* Border */
.registration .UIContentTopper{border:none!important}

/* Center Login Bar */
.registration .UIContentTopper, 
.registration .UIWindowShade{ border:none!important}
.registration #globalContainer #content{margin-top:120px!important}

.registration .UIContentTopper, 
.registration #reg_box label, 
#reg_box #reg div.large_form td.label{ text-shadow:1px 1px 3px white!important;  color:black!important;  vertical-align:middle!important}

.registration #reg_box #reg div.large_form div.field_container select{opacity:0.7!important}

#reg_box #reg div.large_form #birthday_warning, 
#reg_box #reg div.large_form #birthday_warning a{text-shadow:1px 1px 3px black!important; color:white!important}

.registration .field_container input{ font-size:20px !important;  font-family:Georgia!important;  background-color:rgba(229,229,229,.8)!important}
 
#reg_box #reg div.large_form div.reg_btn{opacity:0.8!important}


.fbIndex .loggedout_menubar_container, .login_page .login_page_interstitial, .registration .UIContentTopper, .registration .UIWindowShade {
min-width: 0 !important;
z-index: -1 !important;
padding-top: 10px!important;
padding-right: 10px!important;
padding-bottom: 10px!important;
top: 40% !important;
left: 50% !important;
background-color: 
rgba(119, 105, 105, 0)!important;
}

}
